import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import MesaCard from './MesaCard'; // Asegúrate de que este componente esté configurado para mostrar información de la mesa
import './reservas.css';

const MesasPack = () => {
    const [filter, setFilter] = useState('1'); // Inicialmente, la sucursal central (1)
    const [mesas, setMesas] = useState([]);

    // Función para obtener las mesas desde la API
    const fetchMesas = async () => {
        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/mesa/all');
            const data = await response.json();

            // Asegúrate de que la respuesta sea un array antes de asignar las mesas
            if (Array.isArray(data)) {
                setMesas(data);
            } else {
                console.error('La respuesta de la API no es un array:', data);
                setMesas([]); // Asigna un array vacío en caso de error
            }
        } catch (error) {
            console.error('Error al obtener las mesas:', error);
        }
    };

    // Filtrar mesas según la sucursal seleccionada
    const filteredMesas = mesas.filter(mesa => mesa.sucursal === parseInt(filter, 10));

    // Obtener las mesas al cargar el componente
    useEffect(() => {
        fetchMesas();
    }, []); // Solo se ejecuta al cargar el componente

    return (
        <>
            <Header /> {/* Header al inicio */}
            <section>
                <Container>
                    <Row>
                        <Col lg="12" className="text-center mb-4">
                            <h3 className="menu__title">Mapa de Mesas</h3>
                        </Col>
                        <Col lg="12" className="text-center mb-5">
                            <button
                                className={`filter-btn ${filter === '1' ? 'active__btn' : ''}`}
                                onClick={() => setFilter('1')}
                            >
                                Sucursal Central
                            </button>
                            <button
                                className={`filter-btn ${filter === '2' ? 'active__btn' : ''}`}
                                onClick={() => setFilter('2')}
                            >
                                Sucursal Norte
                            </button>
                        </Col>
                        {filteredMesas.length > 0 ? (
                            filteredMesas.map(mesa => (
                                <Col lg="3" md="4" sm="6" xs="6" key={mesa.id} className="mb-4">
                                    <MesaCard item={mesa} /> {/* Reutiliza MesaCard para mostrar la información de la mesa */}
                                </Col>
                            ))
                        ) : (
                            <Col lg="12" className="text-center">
                                <p>No hay mesas disponibles para la sucursal seleccionada.</p>
                            </Col>
                        )}
                    </Row>
                </Container>
            </section>
            <Footer /> {/* Footer al final */}
        </>
    );
}

export default MesasPack;
