// src/pages/SuccessPage.js

import React, { useEffect, useContext } from 'react';
import { CartContext } from '../components/cart/CartContext';
import axios from 'axios';

const SuccessPage = () => {
    const { cartItems, clearCart } = useContext(CartContext);
    

    useEffect(() => {

        const idCliente = 1; // Reempl치zalo con el ID del cliente autenticado, si es fijo o provisto al inicio de sesi칩n.
        const idEmpleado = 1; // Reemplaza con el ID del empleado asignado
        const idSucursal = 1; // Reemplaza con el ID de la sucursal correspondiente
        const correo = "juan@gmail.com"; // Reemplaza con el correo del cliente

        const total = cartItems.reduce((acc, item) => acc + item.precio * (item.quantity || 1), 0);
        
        const productos = cartItems.map(item => ({
            idAlimento: item.idAlimento,
            costo: item.precio * (item.quantity || 1),
            lugarCompra: 'Sucursal Central',
            noReserva: item.noReserva || null,
        }));

        const realizarCompra = async () => {
            try {
                console.log("Datos de la compra a enviar:", {
                    idCliente,
                    idEmpleado,
                    idSucursal,
                    productos,
                    total,
                    correo
                });
                const response = await axios.post('http://localhost:4000/api/factura/realizar-compra', {
                    idCliente,
                    idEmpleado,
                    idSucursal,
                    productos,
                    total,
                    correo
                });
                console.log("Respuesta del servidor:", response.data);
                clearCart();
            } catch (error) {
                console.error("Error al registrar la compra:", error.response?.data || error.message);
            }
        };



        realizarCompra();
    }, [cartItems, clearCart]);

    return (
        <div>
            <h1>Pago Exitoso</h1>
            <p>Gracias por tu compra. Se ha generado una factura.</p>
        </div>
    );
};

export default SuccessPage;
