import React, { useEffect, useState } from "react";
import { Container, Row, Col } from "reactstrap";
import axios from 'axios'; // Importar axios
import ProductCard from "../product-card/ProductCard";
import "./popular-menu.css";

const PopularMenu = () => {
    const [popularMenuFood, setPopularMenuFood] = useState([]);

    useEffect(() => {
        const fetchPopularMenu = async () => {
            try {
                // Hacer la llamada a la API
                const response = await axios.get('/menugeneral/all');
                // Filtrar los productos para obtener solo el ID 1 de cada tipo de menú
                const filteredProducts = response.data.filter(item => 
                    item.tipoMenuId === 1 || item.tipoMenuId === 2 || item.tipoMenuId === 3 || item.tipoMenuId === 4 || item.tipoMenuId === 5
                ).filter(item => item.id === 1);
                setPopularMenuFood(filteredProducts);
            } catch (error) {
                console.error("Error fetching popular menu:", error);
            }
        };

        fetchPopularMenu();
    }, []); // Dependencias vacías para que se ejecute solo una vez al montar

    return (
        <section className="pt-0">
            <Container>
                <Row>
                    <Col lg='12' className="mb-5">
                        <h2 className="popular__menu-title">Popular food Menu</h2>
                    </Col>
                    {popularMenuFood.map(item => (
                        <Col lg='3' md='4' sm='6' xs='6' className='mb-4' key={item.id}>
                            <ProductCard item={item} />
                        </Col>
                    ))}
                </Row>
            </Container>
        </section>
    );
};

export default PopularMenu;
