// src/Reservas.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import { MapContainer, TileLayer, CircleMarker, Tooltip } from 'react-leaflet';
import './reservas.css';

const Reservas = () => {
    const [sucursal, setSucursal] = useState('');
    const [fecha, setFecha] = useState('');
    const [hora, setHora] = useState('');
    const [mesas, setMesas] = useState([]);
    const [correo, setCorreo] = useState('');
    const [mesaSeleccionada, setMesaSeleccionada] = useState(null);

    // Maneja la búsqueda de mesas disponibles
    const handleBuscarMesas = async () => {
        if (!sucursal || !fecha || !hora) {
            alert('Por favor, completa todos los campos para buscar mesas.');
            return;
        }

        try {
            const response = await axios.post('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                fechaConsulta: fecha,
                horaInicio: hora,
                horaFin: hora,
                sucursal
            });
            setMesas(response.data.result);
        } catch (error) {
            console.error('Error al obtener las mesas:', error);
            alert('Error al obtener las mesas. Por favor, intenta nuevamente.');
        }
    };

    // Maneja la selección de una mesa en el mapa
    const seleccionarMesa = (mesa) => {
        if (mesa.estado === 'ocupada') {
            alert('Esta mesa está ocupada. Por favor, selecciona otra.');
        } else {
            setMesaSeleccionada(mesa);
        }
    };

    // Maneja la reserva de la mesa seleccionada
    const handleRealizarReserva = async () => {
        if (!mesaSeleccionada || !correo) {
            alert('Por favor, selecciona una mesa y proporciona un correo electrónico.');
            return;
        }

        try {
            const clienteResponse = await axios.get(`https://federico-fazbear.onrender.com/api/clientesReserva/${correo}`);
            const idCliente = clienteResponse.data.idCliente;

            if (!idCliente) {
                alert('No se encontró un cliente con este correo.');
                return;
            }

            const response = await axios.post('https://federico-fazbear.onrender.com/api/reserva/compras', {
                codigoMesaReserva: mesaSeleccionada.codigo_mesa,
                id_cliente: idCliente,
                correo,
                cantidadPersonas: null,
                precio: null,
                productos: [],
                horaInicial: hora,
                horaFinal: hora,
                fechaReserva: fecha
            });

            alert('Reserva realizada con éxito');
        } catch (error) {
            console.error('Error al realizar la reserva:', error);
            alert('Hubo un error al realizar la reserva. Inténtalo nuevamente.');
        }
    };

    return (
        <div>
            <Header />
            <main>
                <div className="reservas-container">
                    <h2>Reservas</h2>
                    <div className="mapa-mesas">
                        <div className="formulario-busqueda">
                            <select className="filter-btn select-sucursal" value={sucursal} onChange={(e) => setSucursal(e.target.value)}>
                                <option value="">Seleccione Sucursal</option>
                                <option value="1">Sucursal Central</option>
                                <option value="2">Sucursal Norte</option>
                            </select>

                            <input
                                type="date"
                                className="filter-btn"
                                value={fecha}
                                onChange={(e) => setFecha(e.target.value)}
                            />

                            <input
                                type="time"
                                className="filter-btn"
                                value={hora}
                                onChange={(e) => setHora(e.target.value)}
                            />

                            <button className="filter-btn" onClick={handleBuscarMesas}>Buscar Mesas</button>
                        </div>

                        <div className="mapa">
                            {mesas.length > 0 ? (
                                <MapContainer center={[51.505, -0.09]} zoom={13} style={{ height: '400px', width: '100%' }}>
                                    <TileLayer
                                        url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                                        attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
                                    />
                                    {mesas.map((mesa) => (
                                        <CircleMarker
                                            key={mesa.id}
                                            center={[mesa.latitud, mesa.longitud]} // Asegúrate de tener estos valores en los datos
                                            radius={10}
                                            color={mesa.estado === 'ocupada' ? 'red' : 'lightgray'}
                                            eventHandlers={{
                                                click: () => seleccionarMesa(mesa),
                                            }}
                                        >
                                            <Tooltip>{`Mesa ${mesa.numero}`}</Tooltip>
                                        </CircleMarker>
                                    ))}
                                </MapContainer>
                            ) : (
                                <p>No hay mesas para mostrar.</p>
                            )}
                        </div>
                    </div>

                    {mesaSeleccionada && (
                        <div className="formulario-reserva">
                            <h3>Mesa seleccionada: {mesaSeleccionada.numero}</h3>
                            <input
                                type="email"
                                placeholder="Correo electrónico"
                                value={correo}
                                onChange={(e) => setCorreo(e.target.value)}
                                className="filter-btn"
                            />
                            <button className="filter-btn" onClick={handleRealizarReserva}>Confirmar Reserva</button>
                        </div>
                    )}
                </div>
            </main>
            <Footer />
        </div>
    );
};

export default Reservas;
