import React, { useContext, useState } from 'react';
import { CartContext } from '../cart/CartContext';
import './subcart.css'; // Asegúrate de que este archivo exista en la misma carpeta

const SubCart = () => {
    const { cartItems, removeFromCart } = useContext(CartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({});

    if (cartItems.length === 0) {
        return <div className="empty-subcart">El subcarrito está vacío</div>;
    }

    const handleRemove = (itemId) => {
        const quantityToRemove = quantitiesToRemove[itemId] || 1;
        removeFromCart(itemId, quantityToRemove);
        setQuantitiesToRemove({ ...quantitiesToRemove, [itemId]: 1 });
    };

    const handleQuantityChange = (itemId, value) => {
        setQuantitiesToRemove({ ...quantitiesToRemove, [itemId]: Math.max(1, value) });
    };

    const formatTime = (dateTime) => {
        const options = { hour: '2-digit', minute: '2-digit', hour12: false };
        return new Date(dateTime).toLocaleTimeString([], options);
    };

    return (
        <div className="subcart-container">
            <h2>Subcarrito de Reservas</h2>
            <ul className="subcart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="subcart-item">
                        <img 
                            src={item.imagen || 'ruta_por_defecto'} // Ruta por defecto
                            alt={`Mesa ${item.nombre || item.codigoMesaReserva}`} 
                            className="subcart-item-image" 
                        />
                        <div className="subcart-item-details">
                            <h5>Reserva - Mesa {item.codigoMesaReserva}</h5>
                            <p>Capacidad: {item.cantidadPersonas}</p>
                            <p>Fecha: {item.fechaReserva}</p>
                            <p>Hora: {formatTime(item.horaInicial)} - {formatTime(item.horaFinal)}</p>
                            <p>Precio: ${item.precio}</p>
                            <input
                                type="number"
                                min="1"
                                value={quantitiesToRemove[item.codigoMesaReserva] || 1}
                                onChange={(e) => handleQuantityChange(item.codigoMesaReserva, parseInt(e.target.value))}
                            />
                            <button onClick={() => handleRemove(item.codigoMesaReserva)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="subcart-total">
                <h4>Total: ${cartItems.reduce((acc, item) => acc + item.precio * (item.quantity || 1), 0).toFixed(2)}</h4>
            </div>
        </div>
    );
};

export default SubCart;
