// src/pages/Reservas.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header/Header'; // Importa el Header
import Footer from '../components/footer/Footer'; // Asegúrate de que la ruta sea correcta
import './reservas.css'; // Si tienes un archivo CSS para esta página

const Reservas = () => {
    const [sucursalId, setSucursalId] = useState('');
    const [fecha, setFecha] = useState('');
    const [horaInicial, setHoraInicial] = useState('');
    const [horaFinal, setHoraFinal] = useState('');
    const [mesasDisponibles, setMesasDisponibles] = useState([]);
    const [cantidadPersonas, setCantidadPersonas] = useState(1);
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);

    const sucursales = [
        { id: 1, nombre: 'Sucursal Central' },
        { id: 2, nombre: 'Sucursal Norte' }
    ];

    // Maneja la búsqueda de mesas disponibles
    // Maneja la búsqueda de mesas disponibles
    const handleBuscarMesas = async () => {
        if (!sucursalId || !fecha || !horaInicial || !horaFinal || cantidadPersonas <= 0) {
            console.error("Faltan datos o los datos son incorrectos.");
            return;
        }

        try {
            // Convertir fecha y horas a formato correcto (ISO)
            const fechaISO = new Date(fecha).toISOString().split('T')[0];  // YYYY-MM-DD
            const horaInicialISO = new Date(`${fecha}T${horaInicial}`).toISOString();  // Formato ISO
            const horaFinalISO = new Date(`${fecha}T${horaFinal}`).toISOString();  // Formato ISO

            // Verificar disponibilidad de mesas
            const response = await axios.post('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                sucursal_id: sucursalId,  // Si este campo no es correcto, revisa con la documentación de la API
                fecha: fechaISO,
                hora_inicial: horaInicialISO,
                hora_final: horaFinalISO,
                cantidad_personas: cantidadPersonas
            });

            if (response.status === 200) {
                setMesasDisponibles(response.data);
            } else {
                console.error("Error al obtener las mesas disponibles. Status:", response.status);
            }
        } catch (error) {
            // Muestra información detallada del error
            if (error.response) {
                console.error('Error en la respuesta del servidor:', error.response.data);
            } else if (error.request) {
                console.error('No se recibió respuesta de la API:', error.request);
            } else {
                console.error('Error al configurar la solicitud:', error.message);
            }
        }
    };



    // Maneja la selección de mesa y el proceso de reserva
    const handleReserva = async (codigoMesaReserva, precioMesa) => {
        if (!correo) return;

        try {
            // Verificar el ID del cliente por correo
            const response = await axios.post('https://federico-fazbear.onrender.com/api/login', { correo });
            const clienteId = response.data.id_cliente;
            setIdCliente(clienteId);

            const reservaData = {
                no_reserva: Math.floor(Math.random() * 10000),  // Generar un número de reserva aleatorio
                codigo_mesa: codigoMesaReserva,
                fecha_reserva: new Date(fecha).toISOString().split('T')[0],  // Solo la fecha
                hora_inicial: new Date(`${fecha}T${horaInicial}`).toISOString(),  // Guardar como TIMESTAMP WITH TIME ZONE
                hora_final: new Date(`${fecha}T${horaFinal}`).toISOString(),      // Guardar como TIMESTAMP WITH TIME ZONE
                cantidad_personas: cantidadPersonas,
                precio: precioMesa,
                id_cliente: clienteId,
                correo,
            };

            // Enviar la reserva
            await axios.post('https://federico-fazbear.onrender.com/api/reserva/compras', reservaData);
            alert('¡Reserva realizada con éxito!');

        } catch (error) {
            console.error('Error al completar la reserva:', error);
        }
    };

    return (
        <div>
            <Header />

            <main>
                <div className="reservas-container">
                    <div className="formulario-busqueda">
                        <select
                            className="filter-btn select-sucursal"
                            value={sucursalId}
                            onChange={(e) => setSucursalId(e.target.value)}
                        >
                            <option value="">Seleccione Sucursal</option>
                            {sucursales.map((sucursal) => (
                                <option key={sucursal.id} value={sucursal.id}>{sucursal.nombre}</option>
                            ))}
                        </select>

                        <input
                            type="date"
                            className="filter-btn"
                            value={fecha}
                            onChange={(e) => setFecha(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaInicial}
                            onChange={(e) => setHoraInicial(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaFinal}
                            onChange={(e) => setHoraFinal(e.target.value)}
                        />

                        <input
                            type="number"
                            className="filter-btn"
                            min="1"
                            value={cantidadPersonas}
                            onChange={(e) => setCantidadPersonas(e.target.value)}
                            placeholder="Cantidad de Personas"
                        />

                        <input
                            type="email"
                            className="filter-btn"
                            value={correo}
                            onChange={(e) => setCorreo(e.target.value)}
                            placeholder="Correo"
                        />

                        <button className="filter-btn" onClick={handleBuscarMesas}>Buscar Mesas</button>
                    </div>

                    <div className="mapa-mesas">
                        {mesasDisponibles.length > 0 ? (
                            <div className="mesas-disponibles">
                                {mesasDisponibles.map((mesa) => (
                                    <div key={mesa.codigo} className="mesa">
                                        <p>Mesa {mesa.codigo}</p>
                                        <p>Capacidad: {mesa.capacidad}</p>
                                        <p>Precio: Q{mesa.precio}</p>
                                        <button
                                            onClick={() => handleReserva(mesa.codigo, mesa.precio)}
                                            className="reservar-btn"
                                        >
                                            Reservar
                                        </button>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>No hay mesas disponibles</p>
                        )}
                    </div>
                </div>
            </main>

            <Footer />
        </div>
    );
};

export default Reservas;
