import React, { useEffect } from 'react';
import './LoadingScreen.css';
import soundEffect from '../Multi/carga.mp3'; // Asegúrate de ajustar la ruta

const LoadingScreen = () => {
  useEffect(() => {
    // Crear un objeto de audio usando la ruta importada
    const audio = new Audio(soundEffect);
    audio.play(); // Reproducir el sonido

    // Limpiar el sonido cuando el componente se desmonte
    return () => {
      audio.pause();
      audio.currentTime = 0; // Reiniciar el audio
    };
  }, []);

  return (
    <div className="loading-container">
      <img src={process.env.PUBLIC_URL + '/Logo.png'} alt="Logo" className="loading-logo" />
    </div>
  );
};

export default LoadingScreen;
