import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import MesaCard from './MesaCard'; 
import MesaCardConsulta from './MesaCardConsulta';
import CarritoReserva from './CarritoReserva'; // Importamos el nuevo componente
import antigua from '../Multi/antigua.jpg';
import cayala from '../Multi/cayala.jpg';
import './reservas.css';

const Reservas = () => {
    const [filter, setFilter] = useState('1');
    const [mesas, setMesas] = useState([]);
    const [fechaConsulta, setFechaConsulta] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');
    const [availableTables, setAvailableTables] = useState([]);
    const [selectedTables, setSelectedTables] = useState([]);
    const [showCarrito, setShowCarrito] = useState(false); // Estado para controlar el carrito

    const fetchMesas = async () => {
        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/mesa/all');
            const data = await response.json();

            if (Array.isArray(data.mesas)) {
                setMesas(data.mesas);
            } else {
                setMesas([]);
            }
        } catch (error) {
            console.error('Error al obtener las mesas:', error);
        }
    };

    const filteredMesas = mesas
        .filter(mesa => mesa.Sucursal === parseInt(filter, 10))
        .map(mesa => ({
            ...mesa,
            imagen: filter === '1' ? cayala : antigua
        }));

    useEffect(() => {
        fetchMesas();
    }, []);

    const verificarDisponibilidad = async () => {
        if (!fechaConsulta || !horaInicio || !horaFin) {
            alert("Por favor, completa todos los campos.");
            return;
        }

        const formattedDate = fechaConsulta.split('-').reverse().join('/');

        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ fechaConsulta: formattedDate, horaInicio, horaFin }),
            });

            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Error en la API: ${response.status} - ${errorMessage}`);
            }

            const data = await response.json();

            const ocupadas = new Set(data.result.map(item => item.CODIGO_MESA));

            setAvailableTables(filteredMesas.map(mesa => ({
                ...mesa,
                estado: ocupadas.has(mesa.CodigoMesa) ? 'Ocupada' : 'Disponible',
            })));
        } catch (error) {
            console.error('Error al verificar disponibilidad:', error);
            alert(`Error: ${error.message}`);
        }
    };

    const toggleTableSelection = (mesa) => {
        setSelectedTables(prevSelected => {
            if (prevSelected.includes(mesa)) {
                return prevSelected.filter(item => item !== mesa);
            } else {
                return [...prevSelected, mesa];
            }
        });
    };

    const handleCompleteReserva = () => {
        setShowCarrito(true); // Mostrar el carrito
    };

    const handleCloseCarrito = () => {
        setShowCarrito(false); // Cerrar el carrito
    };

    const handleConfirmReserva = () => {
        // Lógica para confirmar la reserva (puedes implementar esto más adelante)
        alert('Reserva confirmada!');
        setShowCarrito(false); // Cerrar el carrito después de confirmar
    };

    return (
        <>
            <Header />
            <section>
                <Container>
                    <Row>
                        <Col lg="12" className="text-center mb-4">
                            <h3 className="menu__title">Mapa de Mesas</h3>
                        </Col>
                        <Col lg="12" className="text-center mb-5">
                            <button className={`filter-btn ${filter === '1' ? 'active__btn' : ''}`} onClick={() => setFilter('1')}>Sucursal Central</button>
                            <button className={`filter-btn ${filter === '2' ? 'active__btn' : ''}`} onClick={() => setFilter('2')}>Sucursal Norte</button>
                        </Col>
                        {filteredMesas.length > 0 ? (
                            filteredMesas.map(mesa => (
                                <Col lg="3" md="4" sm="6" xs="6" key={mesa.CodigoMesa} className="mb-4">
                                    <MesaCard item={mesa} onSelect={toggleTableSelection} isSelected={selectedTables.includes(mesa)} />
                                </Col>
                            ))
                        ) : (
                            <Col lg="12" className="text-center">
                                <p>No hay mesas disponibles para la sucursal seleccionada.</p>
                            </Col>
                        )}
                        <Col lg="12" className="mb-4 text-center">
                            <h5 className="menu__title">Verificar Disponibilidad</h5>
                            <input type="date" onChange={(e) => setFechaConsulta(e.target.value)} />
                            <input type="time" onChange={(e) => setHoraInicio(e.target.value)} />
                            <input type="time" onChange={(e) => setHoraFin(e.target.value)} />
                            <button onClick={verificarDisponibilidad} className="verificar-btn">Verificar</button>
                        </Col>
                        {/* Mostrar las mesas según su disponibilidad */}
                        {availableTables.length > 0 ? (
                            availableTables.map(mesa => (
                                <Col lg="3" md="4" sm="6" xs="6" key={mesa.CodigoMesa} className="mb-4">
                                    <MesaCardConsulta item={mesa} />
                                </Col>
                            ))
                        ) : (
                            <Col lg="12" className="text-center">
                                <p>No hay mesas disponibles para la sucursal seleccionada.</p>
                            </Col>
                        )}
                        {/* Subcarrito para mostrar mesas seleccionadas */}
                        {selectedTables.length > 0 && (
                            <Col lg="12" className="mt-4">
                                <h5>Mesas Seleccionadas:</h5>
                                <ul>
                                    {selectedTables.map(mesa => (
                                        <li key={mesa.CodigoMesa}>
                                            Mesa {mesa.NumMesa} - Código: {mesa.CodigoMesa}
                                        </li>
                                    ))}
                                </ul>
                            </Col>
                        )}
                        <Col lg="12" className="text-center mt-4">
                            <button onClick={handleCompleteReserva} className="completar-btn">Completar Reserva</button>
                        </Col>
                    </Row>
                </Container>
                {showCarrito && (
                    <CarritoReserva 
                        mesasSeleccionadas={selectedTables}
                        onClose={handleCloseCarrito}
                        onConfirm={handleConfirmReserva}
                    />
                )}
            </section>
            <Footer />
        </>
    );
}

export default Reservas;
