import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header/Header'; // Importa el Header
import Footer from '../components/footer/Footer'; // Asegúrate de que la ruta sea correcta
import './reservas.css'; // Si tienes un archivo CSS para esta página

const Reservas = () => {
    const [sucursalId, setSucursalId] = useState('');
    const [fecha, setFecha] = useState('');
    const [horaInicial, setHoraInicial] = useState('');
    const [horaFinal, setHoraFinal] = useState('');
    const [mesas, setMesas] = useState([]);
    const [mesasDisponibles, setMesasDisponibles] = useState([]);
    const [cantidadPersonas, setCantidadPersonas] = useState(1);
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);
    const [sucursales, setSucursales] = useState([]);

    useEffect(() => {
        // Cargar sucursales desde la base de datos
        const fetchSucursales = async () => {
            try {
                const response = await axios.get('https://federico-fazbear.onrender.com/api/sucursales');
                setSucursales(response.data); // Asegúrate de que la respuesta tenga la estructura esperada
            } catch (error) {
                console.error('Error al obtener las sucursales:', error);
            }
        };

        fetchSucursales();
    }, []);

    useEffect(() => {
        const handleBuscarMesas = async () => {
            if (!sucursalId || !fecha || !horaInicial || !horaFinal) return;

            try {
                // Verificar disponibilidad de mesas
                const response = await axios.get('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                    params: {
                        fecha,
                        hora_inicial: horaInicial,
                        hora_final: horaFinal,
                        sucursal_id: sucursalId,
                    },
                });
                setMesasDisponibles(response.data);

                // Obtener todas las mesas para mostrarlas en el mapa
                const mesasResponse = await axios.get('https://federico-fazbear.onrender.com/api/mesa/all');
                setMesas(mesasResponse.data);
            } catch (error) {
                console.error('Error al obtener las mesas:', error);
            }
        };

        handleBuscarMesas();
    }, [sucursalId, fecha, horaInicial, horaFinal]); // Ahora solo dependemos de los valores relevantes

    const handleReserva = async (codigoMesaReserva) => {
        if (!correo) return;

        // Verificar el ID del cliente
        try {
            const response = await axios.post('https://federico-fazbear.onrender.com/api/login', { correo });
            setIdCliente(response.data.id_cliente); // Asumiendo que el ID del cliente es devuelto en la respuesta

            const reservaData = {
                no_reserva: null, // Aquí puedes establecer el número de reserva si es necesario
                codigo_mesa: codigoMesaReserva,
                fecha_reserva: new Date(fecha).toISOString().split('T')[0],  // Solo la fecha
                hora_inicial: new Date(`${fecha}T${horaInicial}`).toISOString(),  // Guardar como TIMESTAMP WITH TIME ZONE
                hora_final: new Date(`${fecha}T${horaFinal}`).toISOString(),      // Guardar como TIMESTAMP WITH TIME ZONE
                cantidad_personas: cantidadPersonas,
                precio: null,  // Inicialmente nulo
                id_cliente: idCliente,
                correo,
            };

            // Aquí deberías realizar la solicitud para completar la reserva
            console.log('Datos de reserva:', reservaData);
            // const reservaResponse = await axios.post('API_ENDPOINT_DE_RESERVA', reservaData);

        } catch (error) {
            console.error('Error al verificar el cliente:', error);
        }
    };

    return (
        <div>
            <Header />

            <main>
                <div className="reservas-container">
                    <div className="mapa-mesas">
                        <div className="formulario-busqueda">
                            <select
                                className="filter-btn select-sucursal"
                                value={sucursalId}
                                onChange={(e) => setSucursalId(e.target.value)}
                            >
                                <option value="">Seleccione Sucursal</option>
                                {sucursales.map((sucursal) => (
                                    <option key={sucursal.id} value={sucursal.id}>{sucursal.nombre}</option>
                                ))}
                            </select>

                            <input
                                type="date"
                                className="filter-btn"
                                value={fecha}
                                onChange={(e) => setFecha(e.target.value)}
                            />

                            <input
                                type="time"
                                className="filter-btn"
                                value={horaInicial}
                                onChange={(e) => setHoraInicial(e.target.value)}
                            />

                            <input
                                type="time"
                                className="filter-btn"
                                value={horaFinal}
                                onChange={(e) => setHoraFinal(e.target.value)}
                            />

                            <input
                                type="number"
                                className="filter-btn"
                                min="1"
                                value={cantidadPersonas}
                                onChange={(e) => setCantidadPersonas(e.target.value)}
                                placeholder="Cantidad de Personas"
                            />

                            <input
                                type="email"
                                className="filter-btn"
                                value={correo}
                                onChange={(e) => setCorreo(e.target.value)}
                                placeholder="Correo"
                            />

                            <button className="filter-btn" onClick={handleBuscarMesas}>Buscar Mesas</button>
                        </div>

                        <div className="mapa">
                            {mesas.length > 0 ? (
                                mesas.map((mesa) => {
                                    const estaOcupada = mesasDisponibles.includes(mesa.codigo);
                                    return (
                                        <div
                                            key={mesa.codigo}
                                            className={`mesa ${estaOcupada ? 'mesa-ocupada' : 'mesa-libre'}`}
                                            onClick={() => !estaOcupada && handleReserva(mesa.codigo)}
                                        >
                                            {`Mesa ${mesa.numero}`}
                                        </div>
                                    );
                                })
                            ) : (
                                <p>No hay mesas para mostrar.</p>
                            )}
                        </div>
                    </div>
                </div>
            </main>

            <Footer />
        </div>
    );
};

export default Reservas;
