import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { SubCartContext } from '../pages/SubCartContext';
import './subcart.css';

const SubCart = () => {
    const { cartItems, removeFromSubCart } = useContext(SubCartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({});

    console.log("Artículos en el subcarrito:", cartItems); // Debug

    if (cartItems.length === 0) {
        return <div className="empty-cart">El subcarrito está vacío</div>;
    }

    const handleRemove = (itemId) => {
        const quantityToRemove = quantitiesToRemove[itemId] || 1;
        removeFromSubCart(itemId, quantityToRemove);
        setQuantitiesToRemove({ ...quantitiesToRemove, [itemId]: 1 });
    };

    const handleQuantityChange = (itemId, value) => {
        setQuantitiesToRemove({ ...quantitiesToRemove, [itemId]: Math.max(1, value) });
    };

    const formatTime = (dateTime) => {
        const options = { hour: '2-digit', minute: '2-digit', hour12: false };
        return new Date(dateTime).toLocaleTimeString([], options);
    };

    const totalCartAmount = cartItems.reduce((acc, item) => acc + (item.precio * (item.quantity || 1)), 0);

    return (
        <div className="cart-container">
            <h2>Subcarrito</h2>
            <ul className="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                            <>
                                <img 
                                    src={item.imagen || 'ruta_por_defecto'}
                                    alt={`Mesa ${item.nombre || item.codigoMesaReserva}`}
                                    className="cart-item-image" 
                                />
                                <div className="cart-item-details">
                                    <h5>Reserva - Mesa {item.codigoMesaReserva}</h5>
                                    <p>Capacidad: {item.cantidadPersonas}</p>
                                    <p>Fecha de Reserva: {item.fechaReserva}</p>
                                    <p>Hora: {formatTime(item.horaInicio)} - {formatTime(item.horaFin)}</p>
                                    <p>Precio: ${item.precio}</p>
                                    <button onClick={() => handleRemove(item.codigoMesaReserva)}>Eliminar</button>
                                </div>
                            </>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total: Q{totalCartAmount.toFixed(2)}</h4>
            </div>
        </div>
    );
};

const BackToMenuButton = () => (
    <Link to="/" className="back-to-menu">← Volver al Menú</Link>
);

const SubCartPage = () => (
    <div>
        <BackToMenuButton />
        <SubCart />
    </div>
);

export default SubCartPage;
