import React, { useState, useEffect } from 'react';

const Detalle = ({ correoCliente }) => {
    const [reservas, setReservas] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true); // Estado de carga

    useEffect(() => {
        const fetchReservas = async () => {
            setLoading(true); // Inicia la carga
            setError(''); // Reinicia el error

            if (!correoCliente) {
                setLoading(false);
                setError('Correo del cliente no proporcionado.');
                return;
            }

            try {
                // Obtener ID del cliente
                const response = await fetch(`https://federico-fazbear.onrender.com/api/cliente/correo/${correoCliente.trim().toLowerCase()}`);
                
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || 'Error al obtener el ID del cliente');
                }

                const data = await response.json();
                console.log("Datos del cliente:", data); // Verifica la respuesta

                // Asegúrate de que el ID del cliente esté presente
                if (data.cliente && data.cliente.idCliente) {
                    const reservasResponse = await fetch(`https://federico-fazbear.onrender.com/api/reservas/${data.cliente.idCliente}`); // Cambia esta URL si es necesario
                    if (!reservasResponse.ok) {
                        const errorData = await reservasResponse.json();
                        throw new Error(errorData.message || 'Error al cargar las reservas');
                    }
                    const reservasData = await reservasResponse.json();
                    setReservas(reservasData.reservas || []);
                } else {
                    throw new Error('No se encontró el cliente asociado al correo');
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false); // Termina la carga
            }
        };

        fetchReservas();
    }, [correoCliente]);

    if (loading) return <div>Cargando reservas...</div>; // Mensaje de carga

    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h3>Reservas del Cliente</h3>
            {reservas.length > 0 ? (
                <ul>
                    {reservas.map((reserva) => (
                        <li key={reserva.id}>{`Reserva ID: ${reserva.id}, Fecha: ${reserva.fecha}`}</li>
                    ))}
                </ul>
            ) : (
                <p>No hay reservas disponibles.</p>
            )}
        </div>
    );
};

export default Detalle;
