import React from 'react';
import './mesa-card.css';

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, estado, imagen } = item; // Asegúrate de que estos son los campos correctos

    return (
        <div className="single__product">
            {/* Mostrar imagen de la mesa */}
            <img src={item.imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p> {/* Mostrar el código de la mesa */}
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className={`estado ${estado === 'Ocupada' ? 'ocupada' : 'libre'}`}>{estado}</span>
                </div>
                <div className="d-flex align-items-center justify-content-between mt-2">
                    <span className="price">Precio: <strong>Q {precio}</strong></span> {/* Estilo consistente con el resto del texto */}
                </div>
                <button className="add-to-cart-btn mt-3" onClick={handleAddToCart}>
                    Reservar
                </button>
            </div>
        </div>
    )

};

export default MesaCard;
