import React, { useContext } from 'react';
import { SubCartContext } from '../pages/SubCartContext';
import './mesa-card.css';

const MesaCard = ({ item, isSelected, fechaConsulta, horaInicio, horaFin }) => { // Recibe las nuevas props
    const { addToSubCart } = useContext(SubCartContext);

    const { CodigoMesa, NumMesa, Capacidad, imagen } = item;
    const precio = 100;

    const handleAddToCart = () => {
        if (!fechaConsulta || !horaInicio || !horaFin) {
            alert('Por favor, completa la fecha y las horas de inicio y fin antes de seleccionar una mesa.');
            return;
        }

        const reservationItem = {
            codigoMesaReserva: CodigoMesa,
            nombre: `Mesa ${NumMesa}`,
            capacidad: Capacidad,
            precio,
            cantidadPersonas: Capacidad,
            fecha: fechaConsulta,       // Incluye la fecha
            horaInicio: horaInicio,     // Incluye la hora de inicio
            horaFin: horaFin            // Incluye la hora de fin
        };
        
        addToSubCart(reservationItem); // Añade el objeto completo al SubCart
    };

    return (
        <div className={`single__product ${isSelected ? 'selected' : ''}`}>
            <img src={imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className="price d-flex align-items-center">
                        Precio: <span>${precio}</span>
                    </span>
                </div>
                <button onClick={handleAddToCart} className="select-btn">
                    Seleccionar
                </button>
            </div>
        </div>
    );
};

export default MesaCard;
