// src/pages/Reservas.js
import React, { useState } from 'react';
import axios from 'axios';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import './reservas.css';

const Reservas = () => {
    const [fecha, setFecha] = useState('');
    const [horaInicial, setHoraInicial] = useState('');
    const [horaFinal, setHoraFinal] = useState('');
    const [mesasDisponibles, setMesasDisponibles] = useState([]);
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);

    const handleBuscarMesas = async () => {
        if (!fecha || !horaInicial || !horaFinal) {
            console.error("Faltan datos o los datos son incorrectos.");
            return;
        }

        try {
            const response = await axios.post('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                fechaConsulta: fecha,
                horaInicio: horaInicial,
                horaFin: horaFinal,
            });

            setMesasDisponibles(response.data.result);
        } catch (error) {
            if (error.response) {
                console.error('Error en la respuesta del servidor:', error.response.data);
                alert(`Error: ${JSON.stringify(error.response.data)}`);
            } else if (error.request) {
                console.error('No se recibió respuesta de la API:', error.request);
            } else {
                console.error('Error al configurar la solicitud:', error.message);
            }
        }
    };

    const handleReserva = async (codigoMesaReserva, precioMesa) => {
        if (!correo) return;

        try {
            // Verificar el ID del cliente por correo
            const response = await axios.post('https://federico-fazbear.onrender.com/api/login', { correo });
            const clienteId = response.data.id_cliente;
            setIdCliente(clienteId);

            const reservaData = {
                no_reserva: Math.floor(Math.random() * 10000),  // Generar un número de reserva aleatorio
                codigo_mesa: codigoMesaReserva,
                fecha_reserva: new Date(fecha).toISOString().split('T')[0],  // Solo la fecha
                hora_inicial: new Date(`${fecha}T${horaInicial}`).toISOString(),  // Guardar como TIMESTAMP WITH TIME ZONE
                hora_final: new Date(`${fecha}T${horaFinal}`).toISOString(),      // Guardar como TIMESTAMP WITH TIME ZONE
                precio: precioMesa,
                id_cliente: clienteId,
                correo,
            };

            // Enviar la reserva
            await axios.post('https://federico-fazbear.onrender.com/api/reserva/compras', reservaData);
            alert('¡Reserva realizada con éxito!');

        } catch (error) {
            console.error('Error al completar la reserva:', error);
        }
    };

    return (
        <div>
            <Header />

            <main>
                <div className="reservas-container">
                    <div className="formulario-busqueda">
                        <input
                            type="date"
                            className="filter-btn"
                            value={fecha}
                            onChange={(e) => setFecha(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaInicial}
                            onChange={(e) => setHoraInicial(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaFinal}
                            onChange={(e) => setHoraFinal(e.target.value)}
                        />

                        <button className="filter-btn" onClick={handleBuscarMesas}>Buscar Mesas</button>
                    </div>

                    <div className="mapa-mesas">
                        {mesasDisponibles.length > 0 ? (
                            <div className="mesas-disponibles">
                                {mesasDisponibles.map((mesa) => (
                                    <div key={mesa.codigo} className="mesa">
                                        <p>Mesa {mesa.codigo}</p>
                                        <p>Precio: Q{mesa.precio}</p>
                                        <button
                                            onClick={() => handleReserva(mesa.codigo, mesa.precio)}
                                            className="reservar-btn"
                                        >
                                            Reservar
                                        </button>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>No hay mesas disponibles</p>
                        )}
                    </div>
                </div>
            </main>

            <Footer />
        </div>
    );
};

export default Reservas;
