import React, { useState } from 'react';
import './mesa-card.css';
import { useSubCart } from '../pages/SubCartContext'; // Importar el contexto del carrito

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, imagen } = item;
    const precio = 100; // Precio fijo definido
    const [showForm, setShowForm] = useState(false);
    const [correo, setCorreo] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');
    const [fechaReserva, setFechaReserva] = useState('');
    const [idCliente, setIdCliente] = useState(null);
    
    const { addToSubCart } = useSubCart(); // Obtener la función addToCart del contexto del carrito

    // Obtener todos los clientes al montar el componente
    const verificarCliente = async () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }

        try {
            const response = await fetch(`https://federico-fazbear.onrender.com/api/cliente/correo/${correo.trim().toLowerCase()}`);
            
            if (!response.ok) {
                const errorMessage = await response.json();
                alert(`Error: ${errorMessage.message}`);
                setIdCliente(null);
                return;
            }

            const data = await response.json();
            setIdCliente(data.cliente.idCliente);

        } catch (error) {
            console.error('Error al verificar cliente:', error);
            alert('Error al verificar cliente: ' + error.message);
            setIdCliente(null);
        }
    };

    // Función para agregar la reserva al SubCart
    const agregarReservaASubCart = () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }

        if (!fechaReserva) {
            alert('Por favor, seleccione una fecha para la reserva.');
            return;
        }

        if (!horaInicio || !horaFin || horaFin <= horaInicio) {
            alert('Por favor, seleccione una hora de inicio y una hora de fin válidas.');
            return;
        }

        // Objeto de reserva con toda la información para el subcarrito
        const reservaItem = {
            idAlimento: CodigoMesa,
            nombre: `Reserva Mesa ${NumMesa}`,
            quantity: 1,
            precio,
            imagen,
            detalles: {
                fechaReserva,
                horaInicio,
                horaFin,
                correo,
                capacidad: Capacidad,
            }
        };

        // Llamada al contexto para agregar al carrito
        addToSubCart(reservaItem);
        alert('Reserva agregada al subcarrito.');
        setShowForm(false); // Ocultar el formulario después de agregar la reserva
    };

    return (
        <div className="single__product">
            <img src={imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className="price d-flex align-items-center">
                        Precio: <span>${precio}</span>
                    </span>
                </div>
                <button className="btn-reservar" onClick={() => setShowForm(true)}>Reservar</button>

                {showForm && (
                    <div className="reservation-form-container">
                        <button className="btn-close" onClick={() => setShowForm(false)}></button>
                        <div className="reservation-form">
                            <h5>Formulario de Reserva</h5>
                            <input 
                                type="email" 
                                placeholder="Correo electrónico" 
                                value={correo} 
                                onChange={(e) => setCorreo(e.target.value)} 
                                onBlur={verificarCliente} 
                            />
                            <input 
                                type="date" 
                                value={fechaReserva} 
                                onChange={(e) => setFechaReserva(e.target.value)} 
                            />
                            <input 
                                type="time" 
                                value={horaInicio} 
                                onChange={(e) => setHoraInicio(e.target.value)} 
                            />
                            <input 
                                type="time" 
                                value={horaFin} 
                                onChange={(e) => setHoraFin(e.target.value)} 
                            />
                            <button className="btn-submit" onClick={agregarReservaASubCart}>Agregar al Subcarrito</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
};

export default MesaCard;
