// src/pages/Reservas.js
import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import MesaCard from './MesaCard';
import './reservas.css';

const Reservas = () => {
    const [mesas, setMesas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [filter, setFilter] = useState(1); // Filtrando por idTipoMesa, si aplica
    // const { cartItems } = useCart(); // Si no es relevante para mesas, puedes eliminar esta línea

    useEffect(() => {
        const fetchMesas = async () => {
            try {
                const response = await fetch('https://federico-fazbear.onrender.com/api/mesas/all'); // Actualiza la URL para obtener mesas
                if (!response.ok) {
                    throw new Error('Error al cargar las mesas');
                }
                const data = await response.json();
                setMesas(data.mesas); // Asegúrate de que la estructura de datos sea correcta
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false);
            }
        };

        fetchMesas();
    }, []);

    if (loading) return <div>Cargando...</div>;
    if (error) return <div>{error}</div>;

    return (
        <>
            <Header />
            <section>
                <Container>
                    <Row>
                        <Col lg="12" className="text-center mb-4">
                            <h3 className="menu__title">Nuestras Mesas</h3>
                        </Col>
                        <Col lg="12" className="text-center mb-5">
                            <button className={`filter-btn ${filter === 1 ? 'active__btn' : ''}`} onClick={() => setFilter(1)}>Sucursal Central</button>
                            <button className={`filter-btn ${filter === 2 ? 'active__btn' : ''}`} onClick={() => setFilter(2)}>Sucursal Norte</button>
                        </Col>
                        {
                            mesas.filter(item => item.ID_SUCURSAL === filter).map(item => (
                                <Col lg="3" md="4" sm="6" xs="6" key={item.idMesa} className="mb-4"> {/* Cambia idAlimento por idMesa */}
                                    <MesaCard item={item} /> {/* Asegúrate de que ProductCard pueda manejar la información de las mesas */}
                                </Col>
                            ))
                        }
                    </Row>
                </Container>
            </section>
            <Footer />
        </>
    );
}

export default Reservas;
