import React, { useContext, useState } from 'react';
import { SubCartContext } from '../pages/SubCartContext';
import './mesa-card.css';

const MesaCard = ({ item, isSelected }) => {
    const { addToSubCart } = useContext(SubCartContext);

    const { CodigoMesa, NumMesa, Capacidad, imagen } = item;
    const precio = 100;

    // Estado local para fecha de reserva y horas
    const [fechaReserva, setFechaReserva] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');

    const handleAddToCart = () => {
        if (!fechaReserva || !horaInicio || !horaFin) {
            alert('Por favor, completa la fecha y las horas de inicio y fin antes de seleccionar una mesa.');
            return;
        }

        const reservationItem = {
            codigoMesaReserva: CodigoMesa,
            nombre: `Mesa ${NumMesa}`,
            capacidad: Capacidad,
            precio,
            cantidadPersonas: Capacidad,
            fecha: fechaReserva,
            horaInicio: horaInicio,
            horaFin: horaFin
        };

        addToSubCart(reservationItem);
    };

    return (
        <div className={`single__product ${isSelected ? 'selected' : ''}`}>
            <img src={imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>CÃ³digo de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className="price d-flex align-items-center">
                        Precio: <span>${precio}</span>
                    </span>
                </div>
                {/* Inputs para fecha de reserva y horas */}
                <div className="reservation__inputs">
                    <input 
                        type="date" 
                        value={fechaReserva} 
                        onChange={(e) => setFechaReserva(e.target.value)} 
                        placeholder="Fecha de Reserva" 
                    />
                    <input 
                        type="time" 
                        value={horaInicio} 
                        onChange={(e) => setHoraInicio(e.target.value)} 
                        placeholder="Hora de Inicio" 
                    />
                    <input 
                        type="time" 
                        value={horaFin} 
                        onChange={(e) => setHoraFin(e.target.value)} 
                        placeholder="Hora de Fin" 
                    />
                </div>
                <button onClick={handleAddToCart} className="select-btn">
                    Seleccionar
                </button>
            </div>
        </div>
    );
};

export default MesaCard;
