// src/cart/SubCartContext.js
import React, { createContext, useState, useContext } from 'react';

// Crea el contexto del subcarrito
export const SubCartContext = createContext();

// Proveedor del contexto del subcarrito
export const SubCartProvider = ({ children }) => {
    const [subCartItems, setSubCartItems] = useState(() => {
        const storedSubCart = sessionStorage.getItem('subCartItems');
        return storedSubCart ? JSON.parse(storedSubCart) : [];
    });

    const updateSubCart = (items) => {
        try {
            if (!Array.isArray(items)) {
                throw new Error("El subcarrito debe ser un array.");
            }
            setSubCartItems(items);
            sessionStorage.setItem('subCartItems', JSON.stringify(items));
        } catch (error) {
            console.error("Error actualizando el subcarrito:", error);
        }
    };

    const addToSubCart = (item) => {
        console.log("Intentando agregar al subcarrito:", item); // Para depuración
        try {
            setSubCartItems((prevSubCartItems) => {
                const updatedItems = [...prevSubCartItems, item];
                updateSubCart(updatedItems);
                console.log("Subcarrito actualizado:", updatedItems); // Para depuración
                return updatedItems;
            });
        } catch (error) {
            console.error("Error agregando al subcarrito:", error);
        }
    };

    const removeFromSubCart = (itemId) => {
        console.log("Intentando eliminar del subcarrito:", itemId); // Para depuración
        try {
            setSubCartItems((prevSubCartItems) => {
                const updatedItems = prevSubCartItems.filter(item => item.codigoMesaReserva !== itemId);
                updateSubCart(updatedItems);
                console.log("Subcarrito actualizado después de eliminar:", updatedItems); // Para depuración
                return updatedItems;
            });
        } catch (error) {
            console.error("Error eliminando del subcarrito:", error);
        }
    };

    return (
        <SubCartContext.Provider value={{ subCartItems, addToSubCart, removeFromSubCart }}>
            {children}
        </SubCartContext.Provider>
    );
};

// Hook personalizado para usar el contexto del subcarrito
export const useSubCart = () => {
    return useContext(SubCartContext);
};
