// CarritoReserva.js
import React from 'react';
import './carrito-reserva.css'; // Asegúrate de crear un archivo CSS para estilos adicionales.

const CarritoReserva = ({ mesasSeleccionadas, onClose, onConfirm }) => {
    return (
        <div className="carrito-reserva">
            <h4>Carrito de Reservas</h4>
            {mesasSeleccionadas.length > 0 ? (
                <ul>
                    {mesasSeleccionadas.map(mesa => (
                        <li key={mesa.CodigoMesa}>
                            Mesa {mesa.NumMesa} - Código: {mesa.CodigoMesa}
                        </li>
                    ))}
                </ul>
            ) : (
                <p>No hay mesas seleccionadas.</p>
            )}
            <div className="carrito-actions">
                <button onClick={onClose} className="btn-close">Cerrar</button>
                <button onClick={onConfirm} className="btn-confirm">Confirmar Reserva</button>
            </div>
        </div>
    );
};

export default CarritoReserva;
