// src/pages/Reservas.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/header/Header'; 
import Footer from '../components/footer/Footer';
import MapaMesas from '../components/Reserva/MapaMesas'; 
import './reservas.css';

const Reservas = () => {
    const [sucursalId, setSucursalId] = useState('');
    const [fecha, setFecha] = useState('');
    const [horaInicial, setHoraInicial] = useState('');
    const [horaFinal, setHoraFinal] = useState('');
    const [mesas, setMesas] = useState([]);
    const [mesasDisponibles, setMesasDisponibles] = useState([]);
    const [cantidadPersonas, setCantidadPersonas] = useState(1);
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);
    const [sucursales, setSucursales] = useState([]);
    const [reservaCompletada, setReservaCompletada] = useState(null); // Nuevo estado para manejar el número de reserva

    useEffect(() => {
        const fetchSucursales = async () => {
            try {
                const response = await axios.get('https://federico-fazbear.onrender.com/api/sucursales');
                setSucursales(response.data); 
            } catch (error) {
                console.error('Error al obtener las sucursales:', error);
            }
        };

        fetchSucursales();
    }, []);

    const handleBuscarMesas = async () => {
        if (!sucursalId || !fecha || !horaInicial || !horaFinal) return;

        try {
            const response = await axios.post('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                sucursal: parseInt(sucursalId),
                fecha,
                hora_inicial: horaInicial,
                hora_final: horaFinal,
                cantidad_personas: cantidadPersonas,
            });
            setMesasDisponibles(response.data);

            const mesasResponse = await axios.get('https://federico-fazbear.onrender.com/api/mesa/all');
            setMesas(mesasResponse.data);
        } catch (error) {
            console.error('Error al obtener las mesas:', error);
        }
    };

    const handleReserva = async (codigoMesaReserva) => {
        if (!correo) return;

        try {
            // Verificar el ID del cliente
            const response = await axios.post('https://federico-fazbear.onrender.com/api/login', { correo });
            const id_cliente = response.data.id_cliente; 
            setIdCliente(id_cliente);

            const reservaData = {
                no_reserva: null,  // El backend generará este valor automáticamente
                codigo_mesa: codigoMesaReserva,
                fecha_reserva: new Date(fecha).toISOString().split('T')[0],  
                hora_inicial: new Date(`${fecha}T${horaInicial}`).toISOString(),
                hora_final: new Date(`${fecha}T${horaFinal}`).toISOString(),
                cantidad_personas: cantidadPersonas,
                precio: null,  // Depende de la lógica de precios que tengas
                id_cliente: id_cliente,
                correo: correo,
            };

            // Realiza la reserva
            const reservaResponse = await axios.post('https://federico-fazbear.onrender.com/api/reserva/compras', reservaData);
            const reservaCompletada = reservaResponse.data.no_reserva;  // Asumimos que el backend devuelve el número de reserva

            setReservaCompletada(reservaCompletada); // Almacena el número de reserva para mostrarlo
            console.log('Reserva completada, número de reserva:', reservaCompletada);

        } catch (error) {
            console.error('Error al verificar el cliente o realizar la reserva:', error);
        }
    };

    useEffect(() => {
        handleBuscarMesas();
    }, [fecha, horaInicial, horaFinal, sucursalId]);

    return (
        <div>
            <Header />

            <main>
                <div className="reservas-container">
                    <div className="formulario-busqueda">
                        <select
                            className="filter-btn select-sucursal"
                            value={sucursalId}
                            onChange={(e) => setSucursalId(e.target.value)}
                        >
                            <option value="">Seleccione Sucursal</option>
                            <option value="1">Sucursal Central</option>
                            <option value="2">Sucursal Norte</option>
                        </select>

                        <input
                            type="date"
                            className="filter-btn"
                            value={fecha}
                            onChange={(e) => setFecha(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaInicial}
                            onChange={(e) => setHoraInicial(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaFinal}
                            onChange={(e) => setHoraFinal(e.target.value)}
                        />

                        <input
                            type="number"
                            className="filter-btn"
                            min="1"
                            value={cantidadPersonas}
                            onChange={(e) => setCantidadPersonas(e.target.value)}
                            placeholder="Cantidad de Personas"
                        />

                        <input
                            type="email"
                            className="filter-btn"
                            value={correo}
                            onChange={(e) => setCorreo(e.target.value)}
                            placeholder="Correo"
                        />

                        <button className="filter-btn" onClick={handleBuscarMesas}>Buscar Mesas</button>
                    </div>

                    <MapaMesas 
                        mesas={mesas} 
                        mesasDisponibles={mesasDisponibles} 
                        onReserva={handleReserva} 
                        sucursalId={sucursalId} 
                    />

                    {/* Mostrar el número de reserva si existe */}
                    {reservaCompletada && (
                        <div className="reserva-completada">
                            <h3>¡Reserva completada!</h3>
                            <p>Número de Reserva: {reservaCompletada}</p>
                        </div>
                    )}
                </div>
            </main>

            <Footer />
        </div>
    );
};

export default Reservas;
