import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './pages/AuthContext'; // Ajusta la ruta
import './login.css';

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const { login } = useAuth();
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        const response = await fetch('https://federico-fazbear.onrender.com/api/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, password }),
        });

        const data = await response.json();

        if (response.ok) {
            login(data.token); // Usa la función login del contexto
            navigate('/'); // Redirige al inicio
        } else {
            setError(data.error);
        }
    };

    return (
        <div className="login__container">
            {/* ... el resto del formulario de inicio de sesión ... */}
        </div>
    );
};

export default Login;
