import React, { useContext, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { SubCartContext } from '../pages/SubCartContext';
import './subcart.css';

const SubCart = () => {
    const { cartItems, removeFromSubCart, clearSubCart } = useContext(SubCartContext);
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);
    const navigate = useNavigate();

    const verificarCliente = async () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }

        try {
            const response = await fetch(`https://federico-fazbear.onrender.com/api/cliente/correo/${correo.trim().toLowerCase()}`);
            
            if (!response.ok) {
                const errorMessage = await response.json();
                alert(`Error: ${errorMessage.message}`);
                setIdCliente(null);
                return;
            }

            const data = await response.json();
            setIdCliente(data.cliente.idCliente);

        } catch (error) {
            console.error('Error al verificar cliente:', error);
            alert('Error al verificar cliente: ' + error.message);
            setIdCliente(null);
        }
    };

    const realizarReserva = async () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }

        await verificarCliente();

        if (!idCliente) {
            alert('Cliente no encontrado. Verifique el correo electrónico ingresado.');
            return;
        }

        const productos = cartItems.map(item => ({
            costo: item.precio,
            lugarCompra: "Linea",
            codigoMesaDetalle: item.codigoMesaReserva || item.idAlimento,
        }));

        const body = {
            id_cliente: idCliente,
            correo,
            fechaReserva: cartItems[0].fechaReserva, // Tomamos la fecha de reserva del primer item
            productos, // Aquí pasamos los detalles de todos los productos
            horaInicial: new Date(`${cartItems[0].fechaReserva}T${cartItems[0].horaInicio}:00Z`).toISOString(),
            horaFinal: new Date(`${cartItems[0].fechaReserva}T${cartItems[0].horaFin}:00Z`).toISOString(),
        };

        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/reserva/compras', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(body),
            });

            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Error en la API: ${response.status} - ${errorMessage}`);
            }

            const result = await response.json();
            alert('Reserva realizada con éxito: ' + result.message);

            clearSubCart(); // Limpiar el subcarrito después de realizar la reserva
            navigate('/'); // Redirigir al menú principal

        } catch (error) {
            console.error('Error al realizar la reserva:', error);
            alert('Error al realizar la reserva: ' + error.message);
        }
    };

    return (
        <div className="cart-container">
            <h2>Subcarrito</h2>
            <input
                type="email"
                placeholder="Correo del cliente"
                value={correo}
                onChange={(e) => setCorreo(e.target.value)}
            />
            <ul className="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                        <img 
                            src={item.imagen || 'ruta_por_defecto'}
                            alt={item.nombre || `Mesa ${item.codigoMesaReserva}`} 
                            className="cart-item-image" 
                        />
                        <div className="cart-item-details">
                            <h5>{item.nombre || `Reserva Mesa ${item.codigoMesaReserva}`}</h5>
                            <p>Precio: Q{item.precio}</p>
                            <p>Cantidad: {item.quantity}</p>
                            <p>Total: Q{(item.precio * item.quantity).toFixed(2)}</p>
                            <p>Fecha de Reserva: {item.fechaReserva}</p>
                            <p>Hora: {item.horaInicio} - {item.horaFin}</p>
                            <button onClick={() => removeFromSubCart(item.idAlimento || item.codigoMesaReserva)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>
            <button onClick={realizarReserva} className="make-reservation-button">
                Hacer Reserva
            </button>
        </div>
    );
};

const BackToMenuButton = () => (
    <Link to="/" className="back-to-menu">← Volver al Menú</Link>
);

const SubCartPage = () => (
    <div>
        <BackToMenuButton />
        <SubCart />
    </div>
);

export default SubCartPage;
