import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CrearClienteForm from './CrearClienteForm'; // Asegúrate de tener este componente correctamente

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [token, setToken] = useState(localStorage.getItem('token') || null);
    const [error, setError] = useState('');
    const [showRegister, setShowRegister] = useState(false);

    const navigate = useNavigate();

    useEffect(() => {
        const storedToken = localStorage.getItem('token');
        if (storedToken) {
            setToken(storedToken);
        }
    }, []);

    const handleLogin = async (e) => {
        e.preventDefault();
        setError('');

        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            const data = await response.json();

            if (response.ok) {
                setToken(data.token);
                localStorage.setItem('token', data.token);
                localStorage.setItem('correoCliente', username);
                navigate('/user-profile');
            } else {
                setError(data.error || 'Error al iniciar sesión');
            }
        } catch (error) {
            setError('Ocurrió un problema al conectar con el servidor.');
        }
    };

    const handleLogout = () => {
        setToken(null);
        localStorage.removeItem('token');
        localStorage.removeItem('correoCliente');
    };

    const handleBackToHomeClick = () => navigate('/');
    const handleProfileRedirect = () => token && navigate('/user-profile');
    const toggleRegisterForm = () => setShowRegister(!showRegister);

    return (
        <div className="login__container" style={{ backgroundColor: '#141124' }}>
            <div className="back-to-home-button">
                <button onClick={handleBackToHomeClick}><i className="ri-arrow-go-back-line"></i></button>
            </div>
            <div className="form__wrapper">
                <h2 className="text-center">Federico Fazbear</h2>

                {!token ? (
                    <>
                        <form onSubmit={handleLogin}>
                            <div className="form__group">
                                <label>Correo</label>
                                <input
                                    type="text"
                                    placeholder="Ingresa tu correo"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                    required
                                />
                            </div>
                            <div className="form__group">
                                <label>Contraseña</label>
                                <input
                                    type="password"
                                    placeholder="Ingresa tu contraseña"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </div>
                            {error && <div className="alert alert-danger">{error}</div>}
                            <button type="submit" className="btn__secondary">
                                Iniciar sesión
                            </button>
                        </form>
                        <button onClick={toggleRegisterForm} className="btn__secondary">
                            {showRegister ? 'Cancelar Registro' : 'Crear Cuenta'}
                        </button>
                        {showRegister && <CrearClienteForm />}
                    </>
                ) : (
                    <div>
                        <h2 className="text-success">Sesión iniciada</h2>
                        <button onClick={handleProfileRedirect} className="login__icon">
                            Perfil
                        </button>
                        <button className="btn__primary" onClick={handleLogout}>
                            Cerrar sesión
                        </button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default Login;
