import React, { useState, useContext } from 'react';
import { CartContext } from '../components/cart/CartContext';
import './mesa-card.css';

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, estado } = item; // Asegúrate de que estos son los campos correctos
    const { addToCart } = useContext(CartContext);
    const [quantity, setQuantity] = useState(1); // Estado para manejar la cantidad

    const handleAddToCart = () => {
        addToCart({ ...item, quantity }); // Añadir al carrito con la cantidad seleccionada
    };

    return (
        <div className="single__product">
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">Capacidad: <span>{Capacidad}</span></span>
                    <span className={`estado ${estado === 'Ocupada' ? 'ocupada' : 'libre'}`}>{estado}</span>
                </div>
                
                {/* Selector de cantidad */}
                <div className="quantity-selector d-flex align-items-center mt-2">
                    <label htmlFor={`quantity-${NumMesa}`} className="me-2">Cantidad:</label>
                    <input
                        type="number"
                        id={`quantity-${NumMesa}`}
                        min="1"
                        value={quantity}
                        onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value, 10)))}
                        className="form-control w-25"
                    />
                </div>
                <button className="add-to-cart-btn mt-3" onClick={handleAddToCart}>Reservar</button>
            </div>
        </div>
    );
};

export default MesaCard;
