import React, { useEffect, useState } from 'react';
import { Container } from 'reactstrap';
import Slider from 'react-slick';
import { Link } from 'react-router-dom';
import "./slider.css";

const HeroSlider = () => {
    const [menuItems, setMenuItems] = useState([]);
    const [randomImages, setRandomImages] = useState([]);

    const settings = {
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000
    };

    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const response = await fetch('https://federico-fazbear.onrender.com/api/menugeneral/all');
                const data = await response.json();
                setMenuItems(data); // Suponiendo que la API devuelve un array
            } catch (error) {
                console.error('Error fetching menu items:', error);
            }
        };

        fetchMenuItems();
    }, []);

    useEffect(() => {
        if (menuItems.length > 0) {
            // Elegir 3 imágenes aleatorias de menuItems
            const randomIndices = [];
            while (randomIndices.length < 3) {
                const randomIndex = Math.floor(Math.random() * menuItems.length);
                if (!randomIndices.includes(randomIndex)) {
                    randomIndices.push(randomIndex);
                }
            }
            setRandomImages(randomIndices.map(index => menuItems[index].imgUrl));
        }
    }, [menuItems]);

    return (
        <section>
            <Container>
                <Slider {...settings}>
                    {randomImages.map((imgUrl, index) => (
                        <div key={index}>
                            <div className="slider__wrapper d-flex align-items-center justify-content-between pt-5">
                                <div className="slider__content w-50 ps-2">
                                    <h2 className="mb-3">Delicious Food {index + 1}</h2> {/* Puedes personalizar el título */}
                                    <p>Discover our amazing menu items!</p> {/* Descripción general */}
                                    <button className="btn">
                                        <Link to="/menu-pack" style={{ textDecoration: 'none', color: 'inherit' }}>
                                            Explore Food
                                        </Link>
                                    </button>
                                </div>
                                <div className="slider__img w-50">
                                    <img src={imgUrl} alt={`Slide ${index + 1}`} className="w-100" />
                                </div>
                            </div>
                        </div>
                    ))}
                </Slider>
            </Container>
        </section>
    );
};

export default HeroSlider;
