import React from 'react';
import { useSubCart } from '../pages/SubCartContext'; // Asegúrate de que la ruta sea correcta
import './mesa-card.css';

const MesaCard = ({ item, onSelect, isSelected }) => {
    const { subcartAddToCart } = useSubCart(); // Accedemos a la función subcartAddToCart
    const { CodigoMesa, NumMesa, Capacidad, imagen } = item;
    const precio = 100; // Precio fijo definido

    const handleAddToCart = () => {
        const reservationItem = {
            codigoMesaReserva: CodigoMesa,
            nombre: `Mesa ${NumMesa}`,
            capacidad: Capacidad,
            precio,
            cantidadPersonas: Capacidad, // Suponiendo que la cantidad de personas es la misma que la capacidad
        };
        subcartAddToCart(reservationItem); // Agregamos al SubCart
    };

    return (
        <div className={`single__product ${isSelected ? 'selected' : ''}`}>
            <img src={imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className="price d-flex align-items-center">
                        Precio: <span>${precio}</span>
                    </span>
                </div>
                <button onClick={handleAddToCart} className="select-btn">
                    {isSelected ? 'Deseleccionar' : 'Seleccionar'}
                </button>
            </div>
        </div>
    );
};

export default MesaCard;
