// src/pages/Reservas.js
import React, { useState } from 'react';
import axios from 'axios';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import './reservas.css';

const Reservas = () => {
    const [fecha, setFecha] = useState('');
    const [fechaConsulta, setFechaConsulta] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');
    const [mesasDisponibles, setMesasDisponibles] = useState([]);
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);

    const handleBuscarMesas = async () => {
        if (!fechaConsulta || !horaInicio || !horaFin) {
            console.error("Faltan datos o los datos son incorrectos.");
            return;
        }

        // Convertir la fecha en el formato DD/MM/YYYY
        const fechaObj = new Date(fechaConsulta);
        const dia = String(fechaObj.getDate()).padStart(2, '0');
        const mes = String(fechaObj.getMonth() + 1).padStart(2, '0'); // Los meses son 0-indexed
        const anio = fechaObj.getFullYear();
        const fechaConsultaFormatted = `${dia}/${mes}/${anio}`; // Formato DD/MM/YYYY
        console.log('Fecha formateada:', fechaConsultaFormatted); // Verificar la fecha

        try {
            const response = await axios.post('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                fechaConsulta: fechaConsultaFormatted, // Usar la fecha formateada
                horaInicio: horaInicio,
                horaFin: horaFin,
            });
            setMesasDisponibles(response.data.result); // Asegúrate de acceder a los datos correctamente
        } catch (error) {
            console.error('Error en la respuesta del servidor:', error.response ? error.response.data : error.message);
            alert(`Error: ${error.response ? JSON.stringify(error.response.data) : error.message}`); // Mostrar alerta
        }
    };

    const handleReserva = async (codigoMesaReserva, precioMesa) => {
        if (!correo) return;

        try {
            // Verificar el ID del cliente por correo
            const response = await axios.post('https://federico-fazbear.onrender.com/api/login', { correo });
            const clienteId = response.data.id_cliente;
            setIdCliente(clienteId);

            const reservaData = {
                no_reserva: Math.floor(Math.random() * 10000),  // Generar un número de reserva aleatorio
                codigo_mesa: codigoMesaReserva,
                fecha_reserva: new Date(fecha).toISOString().split('T')[0],  // Solo la fecha
                horaInicio: new Date(`${fecha}T${horaInicio}`).toISOString(),  // Guardar como TIMESTAMP WITH TIME ZONE
                horaFin: new Date(`${fecha}T${horaFin}`).toISOString(),      // Guardar como TIMESTAMP WITH TIME ZONE
                precio: precioMesa,
                id_cliente: clienteId,
                correo,
            };

            // Enviar la reserva
            await axios.post('https://federico-fazbear.onrender.com/api/reserva/compras', reservaData);
            alert('¡Reserva realizada con éxito!');

        } catch (error) {
            console.error('Error al completar la reserva:', error);
        }
    };

    return (
        <div>
            <Header />

            <main>
                <div className="reservas-container">
                    <div className="formulario-busqueda">
                        <input
                            type="date"
                            className="filter-btn"
                            value={fechaConsulta}
                            onChange={(e) => setFechaConsulta(e.target.value)}
                        />
                        <input
                            type="time"
                            className="filter-btn"
                            value={horaInicio}
                            onChange={(e) => setHoraInicio(e.target.value)}
                        />

                        <input
                            type="time"
                            className="filter-btn"
                            value={horaFin}
                            onChange={(e) => setHoraFin(e.target.value)}
                        />

                        <button className="filter-btn" onClick={handleBuscarMesas}>Buscar Mesas</button>
                    </div>

                    <div className="mapa-mesas">
                        {mesasDisponibles.length > 0 ? (
                            <div className="mesas-disponibles">
                                {mesasDisponibles.map((mesa) => (
                                    <div key={mesa.codigo_mesa} className="mesa">
                                        <p>Mesa {mesa.codigo_mesa}</p>
                                        <p>Precio: Q{mesa.precio}</p>
                                        <button
                                            onClick={() => handleReserva(mesa.codigo_mesa, mesa.precio)}
                                            className="reservar-btn"
                                        >
                                            Reservar
                                        </button>
                                    </div>
                                ))}
                            </div>
                        ) : (
                            <p>No hay mesas disponibles</p>
                        )}
                    </div>
                </div>
            </main>

            <Footer />
        </div>
    );
};

export default Reservas;
