import React, { useState } from 'react';
import './mesa-card.css';

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, imagen } = item;

    const precio = 100; // Precio fijo definido
    const [showForm, setShowForm] = useState(false); // Estado para mostrar el formulario

    // Función para manejar la reserva (puedes expandir esto según tus necesidades)
    const handleReservar = () => {
        setShowForm(true); // Mostrar el formulario
    };

    const handleSubmit = (event) => {
        event.preventDefault(); // Prevenir el envío del formulario
        // Aquí puedes agregar la lógica para procesar la reserva
        alert(`Reserva realizada para la Mesa ${NumMesa}`);
        setShowForm(false); // Ocultar el formulario después de enviar
    };

    return (
        <div className={`single__product`}>
            <img src={imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className="price d-flex align-items-center">
                        Precio: <span>${precio}</span>
                    </span>
                </div>
                <button onClick={handleReservar} className="btn-reservar">
                    Reservar
                </button>

                {/* Formulario de reserva */}
                {showForm && (
                    <form onSubmit={handleSubmit} className="reservation-form">
                        <h5>Formulario de Reserva</h5>
                        <label>
                            Nombre:
                            <input type="text" required />
                        </label>
                        <label>
                            Correo Electrónico:
                            <input type="email" required />
                        </label>
                        <label>
                            Fecha:
                            <input type="date" required />
                        </label>
                        <label>
                            Hora:
                            <input type="time" required />
                        </label>
                        <button type="submit" className="btn-submit">
                            Confirmar Reserva
                        </button>
                    </form>
                )}
            </div>
        </div>
    );
};

export default MesaCard;
