import React, { useState } from 'react';
import './carrito-reserva.css';

const CarritoReserva = ({ carrito, onClose }) => {
    const [correo, setCorreo] = useState('');
    const [idCliente, setIdCliente] = useState(null);
    const [fechaReserva, setFechaReserva] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');
    const [showForm, setShowForm] = useState(false); // Control para mostrar el formulario

    const verificarCliente = async () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }

        try {
            const response = await fetch(`https://federico-fazbear.onrender.com/api/cliente/correo/${correo.trim().toLowerCase()}`);
            
            if (!response.ok) {
                const errorMessage = await response.json();
                alert(`Error: ${errorMessage.message}`);
                setIdCliente(null);
                return;
            }

            const data = await response.json();
            setIdCliente(data.cliente.idCliente);

        } catch (error) {
            console.error('Error al verificar cliente:', error);
            alert('Error al verificar cliente: ' + error.message);
            setIdCliente(null);
        }
    };

    // Función para realizar la reserva
    const realizarReserva = async () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }

        if (!idCliente) {
            alert('Cliente no encontrado. Verifique el correo electrónico ingresado.');
            return;
        }

        if (!fechaReserva) {
            alert('Por favor, seleccione una fecha para la reserva.');
            return;
        }

        if (!horaInicio || !horaFin || horaFin <= horaInicio) {
            alert('Por favor, seleccione una hora de inicio y una hora de fin válidas.');
            return;
        }

        // Aquí se asume que la primera mesa seleccionada se usará para la reserva
        const mesaSeleccionada = carrito[0]; // Modifica esto si necesitas manejar múltiples mesas
        const { CodigoMesa, NumMesa, Capacidad, imagen } = mesaSeleccionada;
        const precio = 100; // Usa el precio que necesites

        const fechaHoraInicio = new Date(`${fechaReserva}T${horaInicio}:00Z`).toISOString();
        const fechaHoraFin = new Date(`${fechaReserva}T${horaFin}:00Z`).toISOString();

        const body = {
            codigoMesaReserva: CodigoMesa,
            id_cliente: idCliente,
            correo,
            cantidadPersonas: Capacidad,
            precio,
            productos: [{  
                costo: precio,
                lugarCompra: "Linea",  
                codigoMesaDetalle: CodigoMesa
            }],
            horaInicial: fechaHoraInicio,
            horaFinal: fechaHoraFin,
            fechaReserva,
        };

        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/reserva/compras', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(body),
            });

            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Error en la API: ${response.status} - ${errorMessage}`);
            }

            const result = await response.json();
            alert('Reserva realizada con éxito: ' + result.message);
            onClose(); // Cerrar el carrito después de la reserva exitosa

            // Agregar la reserva al carrito
            const reservaItem = {
                idAlimento: CodigoMesa, // Usar un identificador único para la reserva
                nombre: `Reserva Mesa ${NumMesa}`,
                quantity: 1,
                precio,
                imagen,
                detalles: {
                    fechaReserva,
                    horaInicio,
                    horaFin,
                }
            };

            // Aquí puedes agregar la lógica para añadir el item al carrito global
            // addToCart(reservaItem); // Si tienes una función para esto

        } catch (error) {
            console.error('Error al realizar la reserva:', error);
            alert('Error al realizar la reserva: ' + error.message);
        }
    };

    return (
        <div className="carrito-reserva">
            <h4>Carrito de Reservas</h4>
            <ul>
                {carrito.map((mesa, index) => (
                    <li key={index}>
                        <h6>Mesa {mesa.NumMesa}</h6>
                        <p>Código: {mesa.CodigoMesa}</p>
                        <p>Capacidad: {mesa.Capacidad}</p>
                    </li>
                ))}
            </ul>

            {/* Formulario para ingresar datos de reserva */}
            <div className="formulario-reserva">
                <input 
                    type="email" 
                    placeholder="Correo electrónico" 
                    value={correo} 
                    onChange={(e) => setCorreo(e.target.value)} 
                />
                <input 
                    type="date" 
                    value={fechaReserva} 
                    onChange={(e) => setFechaReserva(e.target.value)} 
                />
                <input 
                    type="time" 
                    value={horaInicio} 
                    onChange={(e) => setHoraInicio(e.target.value)} 
                />
                <input 
                    type="time" 
                    value={horaFin} 
                    onChange={(e) => setHoraFin(e.target.value)} 
                />
                <button onClick={verificarCliente}>Verificar Cliente</button>
                <button onClick={realizarReserva}>Confirmar Reserva</button>
            </div>

            <div className="carrito-actions">
                <button className="btn-close" onClick={onClose}>Cerrar</button>
            </div>
        </div>
    );
};

export default CarritoReserva;
