import React, { useState, useEffect } from 'react';
import './mesa-card.css';

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, imagen } = item;

    const precio = 100; // Precio fijo definido
    const [showForm, setShowForm] = useState(false);
    const [correo, setCorreo] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');
    const [fechaReserva, setFechaReserva] = useState('');
    const [clientes, setClientes] = useState([]);
    const [idCliente, setIdCliente] = useState(null);

    // Obtener todos los clientes al montar el componente
    const verificarCliente = async () => {
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }
    
        try {
            const response = await fetch(`https://federico-fazbear.onrender.com/api/cliente/correo/${correo.trim().toLowerCase()}`);
            
            if (!response.ok) {
                const errorMessage = await response.json();
                alert(`Error: ${errorMessage.message}`);
                setIdCliente(null);
                return;
            }
    
            const data = await response.json();
            setIdCliente(data.cliente.idCliente); // Asegúrate de usar el nombre correcto del campo
    
        } catch (error) {
            console.error('Error al verificar cliente:', error);
            alert('Error al verificar cliente: ' + error.message);
            setIdCliente(null);
        }
    };
    
    
    
    // Función para realizar la reserva
    const realizarReserva = async () => {
        // Validar el correo electrónico
        if (!correo) {
            alert('Por favor, ingrese un correo electrónico.');
            return;
        }
    
        // Validar que el cliente existe
        if (!idCliente) {
            alert('Cliente no encontrado. Verifique el correo electrónico ingresado.');
            return;
        }
    
        // Validar el precio
        if (!precio) {
            alert('El precio es requerido.');
            return;
        }
    
        // Validar la fecha de reserva
        if (!fechaReserva) {
            alert('Por favor, seleccione una fecha para la reserva.');
            return;
        }
    
        // Validar hora de inicio
        if (!horaInicio) {
            alert('Por favor, seleccione la hora de inicio.');
            return;
        }
    
        // Validar hora de fin
        if (!horaFin) {
            alert('Por favor, seleccione la hora de fin.');
            return;
        }
    
        // Validar que la hora de fin sea después de la hora de inicio
        if (horaFin <= horaInicio) {
            alert('La hora de fin debe ser posterior a la hora de inicio.');
            return;
        }
    
        // Preparar el cuerpo de la solicitud
        const body = {
            codigoMesaReserva: CodigoMesa,
            id_cliente: idCliente,
            correo,
            cantidadPersonas: Capacidad, // Obtener la cantidad de personas de la mesa
            precio,
            productos: [], // Se puede dejar vacío porque la API lo admite
            horaInicial: horaInicio,
            horaFinal: horaFin,
            fechaReserva
        };
    
        // Mostrar datos de la reserva en la consola para depuración
        console.log('Datos de la reserva:', body);
    
        try {
            const response = await fetch('https://federico-fazbear.onrender.com/api/reserva/compras', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(body),
            });
    
            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Error en la API: ${response.status} - ${errorMessage}`);
            }
    
            const result = await response.json();
            alert('Reserva realizada con éxito: ' + result.message);
            await crearDetalleReserva(result.reserva.no_reserva); // Crear detalles de la reserva después de la reserva principal
            setShowForm(false); // Cerrar el formulario al realizar la reserva
        } catch (error) {
            console.error('Error al realizar la reserva:', error);
            alert('Error al realizar la reserva: ' + error.message);
        }
    };
    
    // Función para crear detalles de la reserva
    const crearDetalleReserva = async (noReserva) => {
        const detalleBody = {
            no_reserva: noReserva,
            codigo_mesa: CodigoMesa,
            costo: precio,
            fecha_compra: new Date().toISOString(),
            lugar_compra: "Linea", // Valor predeterminado
        };

        try {
            // Aquí debes hacer una llamada a la API para crear el detalle de la reserva
            const response = await fetch('https://federico-fazbear.onrender.com/api/detalle-reserva', { // Asegúrate de que esta sea la ruta correcta
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(detalleBody),
            });

            if (!response.ok) {
                const errorMessage = await response.text();
                throw new Error(`Error en la API de detalles: ${response.status} - ${errorMessage}`);
            }

            alert('Detalle de reserva creado con éxito.');
        } catch (error) {
            console.error('Error al crear el detalle de la reserva:', error);
            alert('Error al crear el detalle de la reserva: ' + error.message);
        }
    };

    return (
        <div className={`single__product`}>
            <img src={imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className="price d-flex align-items-center">
                        Precio: <span>${precio}</span>
                    </span>
                </div>
                <button onClick={() => setShowForm(true)}>Reservar</button>

                {showForm && (
                    <div className="form-container">
                        <button className="close-button" onClick={() => setShowForm(false)}>X</button>
                        <h5>Formulario de Reserva</h5>
                        <input 
                            type="email" 
                            placeholder="Correo electrónico" 
                            value={correo} 
                            onChange={(e) => setCorreo(e.target.value)} 
                            onBlur={verificarCliente} 
                        />
                        {/* El campo de cantidad de personas se elimina */}
                        <input 
                            type="date" 
                            value={fechaReserva} 
                            onChange={(e) => setFechaReserva(e.target.value)} 
                        />
                        <input 
                            type="time" 
                            value={horaInicio} 
                            onChange={(e) => setHoraInicio(e.target.value)} 
                        />
                        <input 
                            type="time" 
                            value={horaFin} 
                            onChange={(e) => setHoraFin(e.target.value)} 
                        />
                        <button onClick={realizarReserva}>Confirmar Reserva</button>
                    </div>
                )}
            </div>
        </div>
    );
};

export default MesaCard;
