import React, { useContext } from 'react';
import { CartContext } from '../components/cart/CartContext';
import './mesa-card.css';

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, estado } = item; // Asegúrate de que estos son los campos correctos
    const { addToCart } = useContext(CartContext);

    const handleAddToCart = () => {
        addToCart({ ...item, quantity: 1 }); // Añadir al carrito con una cantidad de 1
    };

    return (
        <div className="single__product">
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p> {/* Mostrar el código de la mesa */}
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className={`estado ${estado === 'Ocupada' ? 'ocupada' : 'libre'}`}>{estado}</span>
                </div>
                <button className="add-to-cart-btn mt-3" onClick={handleAddToCart}>
                    Reservar
                </button>
            </div>
        </div>
    );
};

export default MesaCard;
