import React, { useContext, useState } from 'react';
import { SubCartContext } from '../pages/SubCartContext'; // Asegúrate de que la ruta sea correcta
import './subcart.css'; // Asegúrate de que este archivo exista en la misma carpeta

const SubCart = () => {
    const { subCartItems, removeFromSubCart } = useContext(SubCartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({});

    if (subCartItems.length === 0) {
        return <div className="empty-cart">El subcarrito está vacío</div>;
    }

    const handleRemove = (codigoMesa) => {
        removeFromSubCart(codigoMesa);
    };

    const handleQuantityChange = (codigoMesa, value) => {
        setQuantitiesToRemove({ ...quantitiesToRemove, [codigoMesa]: Math.max(1, value) });
    };

    const formatTime = (dateTime) => {
        const options = { hour: '2-digit', minute: '2-digit', hour12: false };
        return new Date(dateTime).toLocaleTimeString([], options);
    };

    return (
        <div className="cart-container">
            <h2>Subcarrito de Reservas</h2>
            <ul className="cart-items">
                {subCartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                        <img 
                            src={item.imagen || 'ruta_por_defecto'} // Ruta por defecto
                            alt={`Mesa ${item.NumMesa || item.CodigoMesa}`} 
                            className="cart-item-image" 
                        />
                        <div className="cart-item-details">
                            <h5>Reserva - Mesa {item.NumMesa}</h5>
                            <p>Capacidad: {item.Capacidad}</p>
                            <p>Fecha: {item.fechaReserva}</p>
                            <p>Hora: {formatTime(item.horaInicio)} - {formatTime(item.horaFin)}</p>
                            <p>Precio: ${item.precio}</p>
                            <input
                                type="number"
                                min="1"
                                value={quantitiesToRemove[item.CodigoMesa] || 1}
                                onChange={(e) => handleQuantityChange(item.CodigoMesa, parseInt(e.target.value))}
                            />
                            <button onClick={() => handleRemove(item.CodigoMesa)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total: ${subCartItems.reduce((acc, item) => acc + item.precio * (quantitiesToRemove[item.CodigoMesa] || 1), 0).toFixed(2)}</h4>
            </div>
        </div>
    );
};

export default SubCart;
