import React, { useState, useEffect } from 'react';
import './MapaMesas.css'; // Importa el archivo CSS

const MapaMesas = () => {
    const [sucursalId, setSucursalId] = useState(1); // Por defecto, Sucursal Central (id: 1)
    const [mesas, setMesas] = useState([]);
    const [estadoMesas, setEstadoMesas] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Funci贸n para manejar el cambio de sucursal
    const handleSucursalChange = (event) => {
        setSucursalId(event.target.value);
    };

    // Funci贸n para cargar mesas y disponibilidad
    const cargarMesas = async () => {
        setLoading(true);
        setError(null);
        console.time("Tiempo de carga de mesas");
    
        try {
            const response = await fetch('https://federico-fazbear.onrender.com/mesa/all');
            
            if (!response.ok) {
                throw new Error(`Error ${response.status}: ${response.statusText}`);
            }
    
            const data = await response.json();
            console.log("Respuesta de la API para mesas:", data);
    
            // Accede a data.mesas y verifica que sea un array
            if (!Array.isArray(data.mesas)) {
                throw new Error("Los datos de la API no son un array");
            }
    
            const mesasFiltradas = data.mesas.filter(mesa => mesa.sucursal_id == sucursalId);
            setMesas(mesasFiltradas);
    
            const disponibilidadResponse = await fetch('https://federico-fazbear.onrender.com/mesasdisponibles/verificar');
            
            if (!disponibilidadResponse.ok) {
                throw new Error(`Error ${disponibilidadResponse.status}: ${disponibilidadResponse.statusText}`);
            }
    
            const disponibilidadData = await disponibilidadResponse.json();
            setEstadoMesas(disponibilidadData);
        } catch (error) {
            setError(`Error al cargar los datos: ${error.message}`);
            console.error("Error en la carga de datos:", error);
        } finally {
            setLoading(false);
            console.timeEnd("Tiempo de carga de mesas");
        }
    };
    

    // Llamar a cargarMesas cuando cambie la sucursal seleccionada
    useEffect(() => {
        cargarMesas();
    }, [sucursalId]);

    return (
        <div>
            <h1>Mapa de Mesas</h1>
            <label htmlFor="sucursal">Selecciona Sucursal:</label>
            <select id="sucursal" value={sucursalId} onChange={handleSucursalChange}>
                <option value="1">Sucursal Central</option>
                <option value="2">Sucursal Norte</option>
            </select>

            {loading ? (
                <p>Cargando mesas...</p>
            ) : error ? (
                <p>{error}</p>
            ) : (
                <div className="mapa-mesas">
                    {mesas.map((mesa) => {
                        const estadoMesa = estadoMesas.find(item => item.mesa_id === mesa.id);
                        const ocupada = estadoMesa ? estadoMesa.ocupada : false;
                        return (
                            <div 
                                key={mesa.id}
                                className={`mesa ${ocupada ? 'ocupada' : 'libre'}`}
                            >
                                <p>Mesa {mesa.id}</p>
                                <p>Capacidad: {mesa.capacidad}</p>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};

export default MapaMesas;
