import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Detalle.css'; // Archivo CSS para estilos

const Detalle = () => {
    const [idCliente, setIdCliente] = useState('');
    const [reservas, setReservas] = useState([]);
    const [detallesReserva, setDetallesReserva] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchReservas = async () => {
        if (!idCliente) {
            setError('Por favor, ingrese un ID de cliente válido.');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const response = await axios.get(`https://federico-fazbear.onrender.com/api/clientesReserva/${idCliente}/reserva`);
            setReservas(response.data.reservas || []);
        } catch (err) {
            setError('Error al obtener las reservas');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleReservaClick = async (noReserva) => {
        setLoading(true);
        setError('');

        try {
            const response = await axios.get(`https://federico-fazbear.onrender.com/api/detalle_reserva/${noReserva}`);
            setDetallesReserva(response.data.detalles || []);
        } catch (err) {
            setError('Error al obtener los detalles de la reserva');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="detalle-container">
            <h1>Reservas del Cliente</h1>
            <div className="input-group">
                <input
                    type="number"
                    value={idCliente}
                    onChange={(e) => setIdCliente(e.target.value)}
                    placeholder="Ingrese ID del Cliente"
                />
                <button onClick={fetchReservas}>Cargar Reservas</button>
            </div>

            {loading && <p>Cargando...</p>}
            {error && <p className="error-message">{error}</p>}

            <div className="reserva-cards">
                {reservas.length > 0 ? (
                    reservas.map((reserva) => (
                        <div
                            key={reserva.no_reserva}
                            className="single__reserva"
                            onClick={() => handleReservaClick(reserva.no_reserva)}
                        >
                            <div className="reserva__content">
                                <h6>Reserva No: {reserva.no_reserva}</h6>
                                <p>Mesa: {reserva.codigo_mesa}</p>
                                <p>Fecha: {new Date(reserva.fecha_reserva).toLocaleDateString()}</p>
                                <p>Precio: Q{reserva.precio}</p>
                            </div>
                        </div>
                    ))
                ) : (
                    !loading && <p>No se encontraron reservas para este cliente.</p>
                )}
            </div>

            {detallesReserva.length > 0 && (
                <div className="detalles-reserva">
                    <h2>Detalles de la Reserva</h2>
                    {detallesReserva.map((detalle) => (
                        <div key={detalle.id_detalle_reserva} className="detalle-item">
                            <p>Número de Reserva: {detalle.no_reserva}</p>
                            <p>Código de Mesa: {detalle.codigo_mesa}</p>
                            <p>Costo: Q{detalle.costo}</p>
                            <p>Fecha de Compra: {new Date(detalle.fecha_compra).toLocaleString()}</p>
                            <p>Lugar de Compra: {detalle.lugar_compra}</p>
                            <hr />
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Detalle;
