import React, { useRef, useEffect, useState, useMemo } from 'react';
import './header.css';
import { Container } from 'reactstrap';
import { Link } from 'react-router-dom';
import { useCart } from '../cart/CartContext';
import logo from '../../assets/images/logo2.png';

const navLinks = [
    { label: 'MENÚ', url: '/menu-pack' },
    { label: 'RESERVAS', url: '/reservas' },
    { label: 'SUCURSALES', url: '/sucursales' },
    { label: 'AYUDA', url: '/Ayuda' },
];

const Header = () => {
    const menuRef = useRef();
    const { cartItems } = useCart();
    const [searchQuery, setSearchQuery] = useState('');
    const [menuItems, setMenuItems] = useState([]);

    const menuToggle = () => menuRef.current.classList.toggle('active__menu');

    // Efecto para obtener los elementos del menú al montar el componente
    useEffect(() => {
        const fetchMenuItems = async () => {
            try {
                const response = await fetch('https://federico-fazbear.onrender.com/api/menugeneral/all');
                const data = await response.json();
                console.log(data); // Verifica la estructura de los datos aquí
                setMenuItems(data); // Asumiendo que la API retorna un array
            } catch (error) {
                console.error("Error fetching menu items:", error);
            }
        };

        fetchMenuItems();
    }, []);

    // Uso de useMemo para optimizar la búsqueda
    const searchResults = useMemo(() => {
        if (!searchQuery) return []; // Si no hay consulta, retorna vacío

        return menuItems.filter(item =>
            item.nombre.toLowerCase().includes(searchQuery.toLowerCase()) // Filtra por 'nombre'
        );
    }, [searchQuery, menuItems]); // Dependencias para recalcular solo cuando cambian

    const handleSearchChange = (event) => {
        setSearchQuery(event.target.value);
    };

    return (
        <header className="header">
            <Container>
                <div className="navigation">
                    <div className="logo" style={{ display: 'flex', alignItems: 'center' }}>
                        <Link to="/">
                            <img
                                src={logo}
                                alt="Federico Fazbear Logo"
                                style={{ width: '100px', height: 'auto', marginRight: '10px' }}
                            />
                        </Link>
                        <h2><span></span> Federico Fazbear´s</h2>
                    </div>
                    <div className="nav__menu" ref={menuRef}>
                        <div className="nav__list__wrapper d-flex align-items-center gap-5">
                            <ul className="nav__list">
                                {navLinks.map((item, index) => (
                                    <li className="nav__item" key={index}>
                                        <a href={item.url} onClick={menuToggle}>{item.label}</a>
                                    </li>
                                ))}
                            </ul>
                            <div className="menu_right d-flex align-items-center">
                                <div className="custom__search">
                                    <input
                                        type="text"
                                        placeholder="Search..."
                                        value={searchQuery}
                                        onChange={handleSearchChange} // Maneja el cambio en el input
                                    />
                                    <span><i className="ri-search-line"></i></span>
                                    {searchResults.length > 0 && (
                                        <ul className="search-results">
                                            {searchResults.map(result => (
                                                <li key={result.id}>
                                                    <Link to={`/menu/${result.id}`} onClick={menuToggle}>
                                                        {result.nombre} {/* Asegúrate de que el campo sea 'nombre' */}
                                                    </Link>
                                                </li>
                                            ))}
                                        </ul>
                                    )}
                                </div>
                                <div className={`cart__icon`} style={{ marginLeft: '15px' }}>
                                    <Link to="/cart">
                                        <i className="ri-shopping-cart-line" style={{ fontSize: '1.5rem' }}></i>
                                        <span className="cart-count">{cartItems.length}</span>
                                    </Link>
                                </div>
                                <div className="login__icon" style={{ marginLeft: '15px' }}>
                                    <Link to="/login">
                                        <i className="ri-user-line" style={{ fontSize: '1.5rem' }}></i>
                                    </Link>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className="mobile_menu">
                        <span><i className="ri-menu-line" onClick={menuToggle}></i></span>
                    </div>
                </div>
            </Container>
        </header>
    );
};

export default Header;
