import React, { useState, useEffect } from 'react';

const Detalle = ({ correoCliente }) => {
    const [reservas, setReservas] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true); // Estado de carga

    useEffect(() => {
        const fetchReservas = async () => {
            setLoading(true); // Inicia la carga
            setError(''); // Reinicia el error

            // Verifica que el correo no esté vacío antes de hacer la solicitud
            if (!correoCliente) {
                setLoading(false);
                setError('Correo del cliente no proporcionado.');
                return;
            }

            try {
                console.log("Consultando reservas para el correo:", correoCliente); // Debugging

                const response = await fetch(`https://federico-fazbear.onrender.com/api/cliente/correo/${correoCliente.trim().toLowerCase()}`);
                
                if (!response.ok) {
                    const errorData = await response.json();
                    throw new Error(errorData.message || 'Error al cargar las reservas');
                }
                
                const data = await response.json();
                
                // Asegúrate de que 'data.reservas' contenga las reservas que esperas
                if (data.reservas) {
                    setReservas(data.reservas);
                } else {
                    throw new Error('No se encontraron reservas');
                }
            } catch (error) {
                setError(error.message);
            } finally {
                setLoading(false); // Termina la carga
            }
        };

        // Llama a la función solo si 'correoCliente' está definido
        fetchReservas();
    }, [correoCliente]);

    if (loading) return <div>Cargando reservas...</div>; // Mensaje de carga

    if (error) return <div>Error: {error}</div>;

    return (
        <div>
            <h3>Reservas del Cliente</h3>
            {reservas.length > 0 ? (
                <ul>
                    {reservas.map((reserva) => (
                        <li key={reserva.id}>{`Reserva ID: ${reserva.id}, Fecha: ${reserva.fecha}`}</li>
                    ))}
                </ul>
            ) : (
                <p>No hay reservas disponibles.</p>
            )}
        </div>
    );
};

export default Detalle;
