import React from 'react';
import './mesa-card.css';

const MesaCard = ({ item }) => {
    const { CodigoMesa, NumMesa, Capacidad, estado, imagen } = item; // Asegúrate de que estos son los campos correctos

    // Establece el estilo del borde solo si la mesa se está mostrando por su disponibilidad
    const bordeColor = estado === 'Ocupada' ? '2px solid red' : '2px solid green';

    return (
        <div className="single__product" style={{ border: bordeColor }}>
            <img src={item.imagen} alt={`Mesa ${NumMesa}`} className="img-fluid mb-2" />
            <div className="product__content">
                <h6>Mesa {NumMesa}</h6>
                <p>Código de Mesa: <strong>{CodigoMesa}</strong></p>
                <div className="d-flex align-items-center justify-content-between">
                    <span className="price d-flex align-items-center">
                        Capacidad: <span>{Capacidad}</span>
                    </span>
                    <span className={`estado ${estado === 'Ocupada' ? 'ocupada' : 'libre'}`}>{estado}</span>
                </div>
            </div>
        </div>
    );
};

export default MesaCard;
