import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../cart/CartContext';
import './cart.css';

const Cart = () => {
    const { cartItems, removeFromCart, reservas, removeReserva } = useContext(CartContext);
    const [quantitiesToRemove, setQuantitiesToRemove] = useState({});

    const handleRemove = (itemId) => {
        const quantityToRemove = quantitiesToRemove[itemId] || 1;
        removeFromCart(itemId, quantityToRemove);
        setQuantitiesToRemove({ ...quantitiesToRemove, [itemId]: 1 }); // Resetea la cantidad a eliminar después de eliminar
    };

    const handleRemoveReserva = (codigoMesaReserva) => {
        removeReserva(codigoMesaReserva);
    };

    const handleQuantityChange = (itemId, value) => {
        setQuantitiesToRemove({ ...quantitiesToRemove, [itemId]: Math.max(1, value) });
    };

    if (cartItems.length === 0 && reservas.length === 0) {
        return <div className="empty-cart">El carrito está vacío</div>;
    }

    return (
        <div className="cart-container">
            <h2>Carrito</h2>
            <ul className="cart-items">
                {cartItems.map((item, index) => (
                    <li key={index} className="cart-item">
                        <img src={item.imagen} alt={item.nombre} className="cart-item-image" />
                        <div className="cart-item-details">
                            <h5>{item.nombre}</h5>
                            <p>Precio: Q{item.precio}</p>
                            <p>Cantidad: {item.quantity}</p>
                            <p>Total: Q{(item.precio * item.quantity).toFixed(2)}</p>
                            <input
                                type="number"
                                min="1"
                                value={quantitiesToRemove[item.idAlimento] || 1}
                                onChange={(e) => handleQuantityChange(item.idAlimento, parseInt(e.target.value))}
                            />
                            <button onClick={() => handleRemove(item.idAlimento)}>Eliminar</button>
                        </div>
                    </li>
                ))}
            </ul>
            <ul className="cart-items">
                {reservas.map((reserva, index) => (
                    <li key={index} className="cart-item">
                        <div className="cart-item-details">
                            <h5>Reserva para {reserva.cantidadPersonas} personas</h5>
                            <p>Mesa: {reserva.codigoMesaReserva}</p>
                            <p>Hora de inicio: {new Date(reserva.horaInicial).toLocaleString()}</p>
                            <p>Hora de fin: {new Date(reserva.horaFinal).toLocaleString()}</p>
                            <p>Precio: Q{reserva.precio}</p>
                            <button onClick={() => handleRemoveReserva(reserva.codigoMesaReserva)}>Eliminar Reserva</button>
                        </div>
                    </li>
                ))}
            </ul>
            <div className="cart-total">
                <h4>Total Productos: Q{cartItems.reduce((acc, item) => acc + item.precio * item.quantity, 0).toFixed(2)}</h4>
                <h4>Total Reservas: Q{reservas.reduce((acc, reserva) => acc + reserva.precio, 0).toFixed(2)}</h4>
                <h3>Total General: Q{(cartItems.reduce((acc, item) => acc + item.precio * item.quantity, 0) + reservas.reduce((acc, reserva) => acc + reserva.precio, 0)).toFixed(2)}</h3>
            </div>
        </div>
    );
};

const BackToMenuButton = () => (
    <Link to="/" className="back-to-menu">← Volver al Menú</Link>
);

const CartPage = () => (
    <div>
        <BackToMenuButton />
        <Cart />
    </div>
);

export default CartPage;
