import React, { useState, useEffect } from 'react';
import { Container, Row, Col } from 'reactstrap';
import axios from 'axios';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import './reservas.css';

const MesasPack = () => {
    const [sucursal, setSucursal] = useState(1);
    const [mesas, setMesas] = useState([]);
    const [fechaConsulta, setFechaConsulta] = useState('');
    const [horaInicio, setHoraInicio] = useState('');
    const [horaFin, setHoraFin] = useState('');

    useEffect(() => {
        const fetchMesas = async () => {
            try {
                // Primero, obtenemos todas las mesas para la sucursal seleccionada
                const responseMesas = await axios.get('https://federico-fazbear.onrender.com/api/mesa/sucursal/:sucursalId', {
                    params: { sucursalId: sucursal }
                });

                let mesasData = responseMesas.data.mesas;

                // Luego, verificamos la disponibilidad de las mesas llamando a la API 'verificarmesa'
                if (fechaConsulta && horaInicio && horaFin) {
                    const responseDisponibilidad = await axios.post('https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar', {
                        fechaConsulta: fechaConsulta,
                        horaInicio: horaInicio,
                        horaFin: horaFin
                    });

                    const mesasOcupadas = responseDisponibilidad.data.result.map(m => m.codigo_mesa);

                    // Actualizamos el estado de cada mesa según su disponibilidad
                    mesasData = mesasData.map(mesa => ({
                        ...mesa,
                        disponible: !mesasOcupadas.includes(mesa.codigo_mesa)
                    }));
                }

                setMesas(mesasData);
            } catch (error) {
                console.error("Error fetching mesas or checking availability:", error);
            }
        };

        fetchMesas();
    }, [sucursal, fechaConsulta, horaInicio, horaFin]);

    return (
        <>
            <Header />
            <section>
                <Container>
                    <Row>
                        <Col lg="12" className="text-center mb-4">
                            <h3 className="mesas__title">Mesas Disponibles</h3>
                        </Col>
                        <Col lg="12" className="text-center mb-5">
                            <div className="filters">
                                <select onChange={(e) => setSucursal(e.target.value)}>
                                    <option value="1">Sucursal Central</option>
                                    <option value="2">Sucursal Norte</option>
                                </select>
                                <input
                                    type="date"
                                    value={fechaConsulta}
                                    onChange={(e) => setFechaConsulta(e.target.value)}
                                    placeholder="Fecha"
                                />
                                <input
                                    type="time"
                                    value={horaInicio}
                                    onChange={(e) => setHoraInicio(e.target.value)}
                                    placeholder="Hora de Inicio"
                                />
                                <input
                                    type="time"
                                    value={horaFin}
                                    onChange={(e) => setHoraFin(e.target.value)}
                                    placeholder="Hora de Fin"
                                />
                            </div>
                        </Col>
                        <Col lg="12">
                            <div className="map-container">
                                {mesas.map(mesa => (
                                    <MesaCard
                                        key={mesa.id}
                                        mesa={mesa}
                                    />
                                ))}
                            </div>
                        </Col>
                    </Row>
                </Container>
            </section>
            <Footer />
        </>
    );
}

export default MesasPack;
