import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Detalle.css'; // Asegúrate de crear este archivo para los estilos

const Detalle = () => {
    const [idCliente, setIdCliente] = useState(localStorage.getItem('clientId') || ''); // Obtener ID del cliente desde el localStorage
    const [reservas, setReservas] = useState([]);
    const [detallesReserva, setDetallesReserva] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const fetchReservas = async () => {
        if (!idCliente) return;

        setLoading(true);
        setError('');

        try {
            const response = await axios.get(`https://federico-fazbear.onrender.com/api/clientesReserva/${idCliente}/reserva`);
            setReservas(response.data.reservas);
        } catch (err) {
            setError('Error al obtener las reservas');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleReservaClick = async (noReserva) => {
        setLoading(true);
        setError('');

        try {
            const response = await axios.get(`https://federico-fazbear.onrender.com/api/detalle_reserva/${noReserva}`);
            setDetallesReserva(response.data.detalles);
        } catch (err) {
            setError('Error al obtener los detalles de la reserva');
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const handleLoadReservas = () => {
        fetchReservas();
    };

    useEffect(() => {
        // Puedes comentar o eliminar esto si no quieres cargar reservas automáticamente al cargar el componente
        // fetchReservas();
    }, [idCliente]); // Ejecutar la función cuando cambia el ID del cliente

    return (
        <div className="reservas-container">
            <h1>Reservas del Cliente</h1>

            <button onClick={handleLoadReservas} className="btn__primary">Cargar Reservas</button>
            
            {loading && <p>Cargando...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <div className="reservas-grid">
                {reservas.map((reserva) => (
                    <div className="reserva-card" key={reserva.no_reserva} onClick={() => handleReservaClick(reserva.no_reserva)}>
                        <h3>Reserva No: {reserva.no_reserva}</h3>
                        <p> Mesa: {reserva.codigo_mesa}</p>
                        <p> Fecha: {new Date(reserva.fecha_reserva).toLocaleDateString()}</p>
                        <p> Precio: ${reserva.precio}</p>
                    </div>
                ))}
            </div>

            {detallesReserva.length > 0 && (
                <div>
                    <h2>Detalles de la Reserva</h2>
                    {detallesReserva.map((detalle) => (
                        <div key={detalle.id_detalle_reserva}>
                            <p>Número de Reserva: {detalle.no_reserva}</p>
                            <p>Código de Mesa: {detalle.codigo_mesa}</p>
                            <p>Costo: ${detalle.costo}</p>
                            <p>Fecha de Compra: {new Date(detalle.fecha_compra).toLocaleString()}</p>
                            <p>Lugar de Compra: {detalle.lugar_compra}</p>
                            <hr />
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Detalle;
