import React from 'react';
import Header from '../components/header/Header';
import Footer from '../components/footer/Footer';
import { useNavigate } from 'react-router-dom';
import Slider from 'react-slick'; // Importa el componente Slider
import './sucursales.css';
import antigua from '../Multi/antigua.jpg';
import cayala from '../Multi/cayala.jpg';

const sucursalesData = [
    {
        id: 1,
        nombre: 'Sucursal Centro',
        direccion: 'Av. Principal 123, Ciudad',
        telefono: '(123) 456-7890',
        horario: 'Lunes a Viernes: 9:00 AM - 8:00 PM',
        imagen: antigua,
    },
    {
        id: 2,
        nombre: 'Sucursal Norte',
        direccion: 'Calle Secundaria 456, Ciudad',
        telefono: '(123) 456-7891',
        horario: 'Lunes a Sábado: 10:00 AM - 6:00 PM',
        imagen: cayala,
    },
];

const Sucursales = () => {
    const navigate = useNavigate();

    const handleReservarClick = () => {
        navigate('/Reservas');
    };

    // Configuración del slider para las imágenes
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
    };

    return (
        <div>
            <Header />

            <main>
                <div className="sucursales-container">
                    <div className="sucursales-list">
                        {sucursalesData.map((sucursal) => (
                            <div key={sucursal.id} className="sucursal-card">
                                <h3>{sucursal.nombre}</h3>
                                <p><strong>Dirección:</strong> {sucursal.direccion}</p>
                                <p><strong>Teléfono:</strong> {sucursal.telefono}</p>
                                <p><strong>Horario:</strong> {sucursal.horario}</p>
                                <button className="reservar-btn" onClick={handleReservarClick}>
                                    Hacer Reserva
                                </button>

                                {/* Slider solo para las imágenes */}
                                <Slider {...settings}>
                                    <div>
                                        <img 
                                            src={sucursal.imagen} 
                                            alt={`Imagen de ${sucursal.nombre}`} 
                                            className="sucursal-imagen" 
                                        />
                                    </div>
                                </Slider>
                            </div>
                        ))}
                    </div>
                </div>
            </main>

            <Footer />
        </div>
    );
};

export default Sucursales;
