import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext'; // Ajusta la ruta
import './login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth(); // Usa la función login del contexto
  const navigate = useNavigate(); // Usa el hook useNavigate

  // Manejar el inicio de sesión
  const handleLogin = async (e) => {
      e.preventDefault();
      setError('');

      const response = await fetch('https://federico-fazbear.onrender.com/api/login', {
          method: 'POST',
          headers: {
              'Content-Type': 'application/json',
          },
          body: JSON.stringify({ username, password }),
      });

      const data = await response.json();

      if (response.ok) {
          login(data.token); // Usa la función login del contexto
          navigate('/'); // Redirige al inicio
      } else {
          setError(data.error || 'Error al iniciar sesión.');
      }
  };

  // Cerrar sesión
  const handleLogout = () => {
      // Esta función ahora es manejada en el Header usando el contexto
  };

  return (
      <div className="login__container">
          <h2 className="text-center">Iniciar Sesión</h2>
          <form onSubmit={handleLogin}>
              <div className="form__group">
                  <label>Correo</label>
                  <input
                      type="text"
                      placeholder="Ingresa tu correo"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      required
                  />
              </div>

              <div className="form__group">
                  <label>Contraseña</label>
                  <input
                      type="password"
                      placeholder="Ingresa tu contraseña"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      required
                  />
              </div>

              {error && <div className="alert alert-danger">{error}</div>}

              <button type="submit" className="btn__primary">
                  Iniciar sesión
              </button>
          </form>
      </div>
  );
};

export default Login;
