import React, { useState, useEffect } from 'react';
import Header from '../components/header/Header'; // Importa el Header
import Footer from '../components/footer/Footer'; // Asegúrate de que la ruta sea correcta
import './reservas.css'; // Importa el archivo CSS para estilos

const Reservas = () => {
    const [fecha, setFecha] = useState('');
    const [horaInicial, setHoraInicial] = useState('');
    const [horaFinal, setHoraFinal] = useState('');
    const [sucursal, setSucursal] = useState('');
    const [mesas, setMesas] = useState([]);
    const [mesasDisponibles, setMesasDisponibles] = useState([]);
    const [mesaSeleccionada, setMesaSeleccionada] = useState(null);
    const [correo, setCorreo] = useState('');

    useEffect(() => {
        console.log("Cargando todas las mesas...");
        obtenerTodasLasMesas();
    }, []);

    const obtenerTodasLasMesas = async () => {
        try {
            const response = await fetch(`https://federico-fazbear.onrender.com/api/mesa/all`);
            const data = await response.json();
            console.log("Mesas obtenidas:", data);
            setMesas(data);
        } catch (error) {
            console.error('Error al obtener mesas:', error);
            alert('Hubo un error al cargar las mesas. Intenta nuevamente.');
        }
    };

    const verificarMesasDisponibles = async () => {
        try {
            console.log("Verificando mesas disponibles...");
            const response = await fetch(`https://federico-fazbear.onrender.com/api/mesasdisponibles/verificar`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    fecha,
                    hora_inicial: horaInicial,
                    hora_final: horaFinal,
                    sucursal
                })
            });
            const data = await response.json();
            console.log("Mesas disponibles:", data);
            setMesasDisponibles(data);
        } catch (error) {
            console.error('Error al verificar mesas disponibles:', error);
            alert('Hubo un error al verificar las mesas disponibles.');
        }
    };

    const seleccionarMesa = (mesa) => {
        console.log(`Mesa seleccionada: ${mesa.codigo}`);
        setMesaSeleccionada(mesa);
        alert(`Mesa ${mesa.codigo} seleccionada. Procede a ingresar el correo para completar la reserva.`);
    };

    const completarReserva = async () => {
        const idCliente = await obtenerIdCliente(correo);

        if (idCliente && mesaSeleccionada) {
            const reserva = {
                no_reserva: generarNumeroReserva(),
                codigo_mesa: mesaSeleccionada.codigo,
                fecha_reserva: fecha,
                hora_inicial: horaInicial,
                hora_final: horaFinal,
                cantidad_personas: 4,
                precio: null,
                id_cliente: idCliente,
                correo: correo
            };

            try {
                console.log("Intentando completar la reserva:", reserva);
                const response = await fetch('https://federico-fazbear.onrender.com/api/reserva/crear', {
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json'
                    },
                    body: JSON.stringify(reserva)
                });
                const result = await response.json();
                console.log("Reserva completada:", result);
                alert('Reserva completada con éxito');
            } catch (error) {
                console.error('Error al completar la reserva:', error);
                alert('Error al completar la reserva. Inténtalo de nuevo.');
            }
        } else {
            alert('No se pudo completar la reserva. Verifique los datos.');
        }
    };

    const obtenerIdCliente = async (correo) => {
        try {
            console.log("Obteniendo ID del cliente para:", correo);
            const response = await fetch(`https://federico-fazbear.onrender.com/api/login`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ correo })
            });
            const data = await response.json();
            console.log("ID del cliente obtenido:", data.id_cliente);
            return data.id_cliente;
        } catch (error) {
            console.error('Error al obtener id del cliente:', error);
            alert('Error al obtener el ID del cliente.');
        }
    };

    const generarNumeroReserva = () => {
        return `RES-${Date.now()}`;
    };

    return (
        <div>
            <Header />
            <h1>Reserva de Mesas</h1>
            <form onSubmit={(e) => { e.preventDefault(); verificarMesasDisponibles(); }}>
                <label>Fecha:</label>
                <input type="date" value={fecha} onChange={(e) => setFecha(e.target.value)} required />

                <label>Hora Inicial:</label>
                <input type="time" value={horaInicial} onChange={(e) => setHoraInicial(e.target.value)} required />

                <label>Hora Final:</label>
                <input type="time" value={horaFinal} onChange={(e) => setHoraFinal(e.target.value)} required />

                <label>Sucursal:</label>
                <select value={sucursal} onChange={(e) => setSucursal(e.target.value)}>
                    <option value="1">Sucursal 1</option>
                    <option value="2">Sucursal 2</option>
                </select>

                <button type="submit">Buscar Mesas</button>
            </form>

            <div id="mapa-mesas" className="mapa-mesas">
                {mesas
                    .filter(mesa => mesa.sucursal === sucursal)
                    .map(mesa => {
                        const estaOcupada = mesasDisponibles.some(m => m.codigo === mesa.codigo);
                        return (
                            <div
                                key={mesa.codigo}
                                className="mesa"
                                style={{ backgroundColor: estaOcupada ? 'red' : 'lightgray' }}
                                onClick={() => seleccionarMesa(mesa)}
                            >
                                {mesa.codigo}
                            </div>
                        );
                    })}
            </div>

            <button onClick={completarReserva}>Completar Reserva</button>
            <Footer />
        </div>
    );
};

export default Reservas;
