// src/components/mapa/MapaMesas.js
import React, { useEffect, useState } from 'react';
import './mapamesas.css'; // Asegúrate de crear este archivo CSS o usar uno existente

const MapaMesas = ({ mesas, mesasDisponibles, onReserva, sucursalId }) => {
    const [mesasSucursal, setMesasSucursal] = useState([]);

    useEffect(() => {
        // Filtrar las mesas según la sucursal seleccionada
        if (sucursalId) {
            const mesasFiltradas = mesas.filter(mesa => mesa.sucursal_id === sucursalId);
            setMesasSucursal(mesasFiltradas);
        } else {
            setMesasSucursal(mesas); // Si no hay sucursal seleccionada, mostrar todas las mesas
        }
    }, [mesas, sucursalId]);

    return (
        <div className="mapa">
            {mesasSucursal.length > 0 ? (
                mesasSucursal.map((mesa) => {
                    const estaOcupada = mesasDisponibles.some(m => m.codigo === mesa.codigo);
                    return (
                        <div
                            key={mesa.codigo}
                            className={`mesa ${estaOcupada ? 'mesa-ocupada' : 'mesa-libre'}`}
                            onClick={() => !estaOcupada && onReserva(mesa.codigo)}
                        >
                            {`Mesa ${mesa.numero}`}
                        </div>
                    );
                })
            ) : (
                <p>No hay mesas para mostrar en esta sucursal.</p>
            )}
        </div>
    );
};

export default MapaMesas;
